INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('아나바시정', '197200030', '서울약품공업(주)', '1정중|아데노실코발라민|별규|1000|마이크로그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="1. 주효능효과">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(1) 비타민 B12결핍증(악성빈혈)의 예방 및 치료]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(2) 비타민 B12의 수요가 증대하여 음식중에서 섭취가 불충분한 경우의 보급]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(소모성질환, 갑상선기능항진증 임산부, 수유부 등)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(3) 거적아구성 빈혈(주사)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(4) 광절열두조충증(주사)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(5) 악성빈혈에 수반된 신경장애(주사)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(6) 흡수부전증후군(스프루등) (주사)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 질환에도 사용할 수 있다.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(1) 거적아구성빈혈(경구)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(2) 광절열두조충증(경구)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(3) 악성빈혈에 수반된 신경장애 (경구)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(4) 흡수부전증후군(스프루등) (경구)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(5) 비타민B12의 결핍 또는 대사 장애가 관여한다고 생각되는 경우의]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[a. 영양성 및 임신성 빈혈]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[b. 위 또는 회장절제 후의 빈혈]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[c. 간장해에 수반된 빈혈]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[d. 방사선에 의한 백혈구감소증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[e. 신경통]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[f. 말초신경염 말초신경마비]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[g. 근육통, 관절통]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[h. 중추신경장애(척수염, 변성질환 등)]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(경구) : 아데노실비12로서 보통 성인 1일 1,500ug까지 1~3회 분할경우 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(주사) : 아데노실비12로서 보통 성인 1일 500 ~ 1,000ug까지 근육주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 지시된 용법용량의 범위내에서는 안전하게 사용할 수 있으나 유효하리라고 추정되는 적응증에 대해서 효과가 없는데 월여에 걸쳐 장기 투여할 필요는 없다." />
    <ARTICLE title="2. 거적아구성 빈혈, 광절열두조충증, 악성빈혈에 수반된 신경장애, 흡수부전증후군(스프루 등) 위 및 회장절제후의 빈혈에 대한 사용은 경구 투여시 흡수가 좋지 못하므로 가능한 주사투여가 바람직하다." />
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('대한젖산나트륨염화나트륨포도당주사액(1-2-3)', '197200036', '대한약품공업(주)', '1000밀리리터중|젖산나트륨액|USP|6.4|그램|;1000밀리리터중|포도당|KP|25|그램|;1000밀리리터중|염화나트륨|KP|3|그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[&nbsp;]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[○ 탈수증, 수술전후 등의 수분ㆍ전해질 보급]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[○ 에너지 보급]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[&nbsp;]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[보통 성인 1회 500～1,000mL를 점적 정맥주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[투여속도는 시간당 300～500mL(분당 75～120방울)이고, 소아의 경우 시간당 50～100mL로 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[투여량, 투여속도는 연령, 체중, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상의주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 경고">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[포도당 함유제제를 정맥주사하는 환자는 치아민(비타민 B<sub>1</sub>) 소모율이 높기 때문에 순간적으로 치명적인 치아민 결핍을 초래할 가능성이 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 투여하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 젖산혈증 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 수분과다상태 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 저장성 탈수증 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 저칼륨혈증 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 고나트륨혈증 환자]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 다음 환자에는 신중히 투여할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 신질환에서 기인한 신부전 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 심부전 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 당뇨병 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 폐쇄성 요로질환에 의한 요량감소가 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 중증 간장애 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 저나트륨혈증(120 mmol/L 미만) 환자]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 이상반응">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[대량ㆍ급속투여에 의해 뇌부종, 폐부종, 말초부종이 나타날 수 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 일반적 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[혈당, 혈청 전해질, 체액 평형을 정기적으로 모니터링해야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 임부, 수유부, 가임여성, 신생아, 유아, 소아, 고령자에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 임신 중 투여에 대한 안전성이 확립되지 않았으므로 치료상의 유익성이 태아에 대한 잠재적 위해성을 상회한다고 판단되는 경우에만 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 분만 중에 포도당을 함유한 용액을 정맥 투여할 경우, 산모에게 고혈당증을 초래할 수 있으며, 신생아의 반동저혈당증 뿐만 아니라, 태아의 고혈당증 및 대사성 산증을 일으킬 수 있다. 태아의 고혈당증은 태아의 인슐린 수치를 증가시킬 수 있으며, 이는 출산 후 신생아 저혈당증을 초래할 수 있다. 해당 약물을 투여하기 전에 환자에 대한 유익성과 위해성을 고려해야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 신생아(특히 조산아나 저체중아의 경우)는 저혈당증 또는 고혈당증의 위험이 높아진다. 그러므로 잠재적인 장기간 부작용을 피하기 위한 적절한 혈당 조절을 할 수 있도록 면밀한 모니터링이 필요하다. 신생아의 저혈당증은 발작, 혼수, 뇌손상을 초래할 수 있다. 고혈당증은 뇌실내출혈, 박테리아 및 곰팡이 감염의 후기발병, 미숙아의 망막병증, 괴사성작은창자큰창자염, 기관지폐이형성증, 입원기간의 연장 및 사망과 관련된다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 적용상의 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 완전히 맑지 않은 것은 사용하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 용기에 있는 공기로 인한 공기색전증의 가능성을 피하기 위하여 연속하여 연결해서는 안 된다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 보관 및 취급상의 주의사항">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[실온에서 보관한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('기모타부정(수출명:케이티정)', '197200084', '제일약품(주)', '이 약 1정 (174.25mg) 중|브로멜라인|KP|40|밀리그램|;이 약 1정 (174.25mg) 중|결정트립신|USP|1|밀리그램|;이 약 1정 (174.25mg) 중|브로멜라인|KP|40|밀리그램|;이 약 1정 (174.25mg) 중|결정트립신|USP|1|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[&nbsp;]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[다음 질환 및 증상의 염증성 부종의 완화 : 수술 및 외상후, 유즙울체]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[성인 : 첫날 1회 2정, 1일 4회, 이후 유지량으로 1회 1정, 1일 4회 복용한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[체내에서의 작용기전은 아직 해명되지 않은 점이 많고 용량, 효과관계도 밝혀진 것이 아니므로 목적 없이 투여하지 않는다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 다음과 같은 사람은 이 약을 복용하기 전에 의사, 치과의사, 약사와 상의할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 혈액응고이상 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 심한 간장애 또는 신장애 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 임부 및 임신하고 있을 가능성이 있는 여성(임신중 투여에 관한 안전성이 확립되어 있지 않으므로 치료상의 유익성이 위험성을 상회한다고 판단되는 경우에만 투여한다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 항응고제를 투여하고 있는 환자(병용투여에 의해, 항응고제의 작용이 증강될 수 있으므로 관찰을 충분히 하고 신중히 투여한다.)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음과 같은 경우 이 약의 복용을 즉각 중지하고 의사, 치과의사, 약사와 상의할 것. 상담시 가능한한 이 첨부문서를 소지할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 과민반응 : 발진, 발적 등이 나타날 수 있으므로 이러한 증상이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 소화기계 : 설사, 변비, 때때로 식욕부진, 배가 거북함, 구역, 구토 등의 증상이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 혈액계 : 드물게 코피, 혈액가래 등의 출혈 경향이 나타날 수 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 기타 이 약의 복용시 주의할 사항">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[정해진 용법·용량을 지킬 것.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 저장상의 주의사항">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 어린이의 손이 닿지 않는 곳에 보관할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 의약품을 원래 용기에서 꺼내어 다른 용기에 보관하는 것은 의약품 오용에 의한 사고 발생이나 의약품 품질 저하의 원인이 될 수 있으므로 원래의 용기에 넣고 꼭 닫아 보관할 것.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('보스민액(에피네프린)', '197200086', '제일약품(주)', '100밀리리터 중|에피네프린|KP|0.1|그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[&nbsp;]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[기관지 천식 발작의 완화, 국소 부위의 출혈 (외상, 코, 입, 인후두 점막의 출혈)]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="1. 기관지천식 : 0.1% 용액을 5-10배로 희석해서 흡입한다(1회 투여량은 0.3mg 이내). 2-15분이 지나도 효과가 불충분할 경우에는 한번 더 반복한다. 단, 연속 사용이 꼭 필요한 경우에는 4-6시간의 간격을 두어 사용한다." />
    <ARTICLE title="2. 국소 출혈 : 0.1% 용액을 5-10배로 희석해서 직접 도포하거나 분무한다." />
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 다음 환자에는 투여하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[폐쇄각 또는 전방이 얇아서 안압 상승의 요인이 있는 환자(안주위부 등에 사용시 폐쇄각 녹내장 환자의 발병을 촉진할 수 있다)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 신중히 투여할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약에 과민증의 병력이 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 교감신경 효능약에 과민증 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 폐부종 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 갑상선기능항진증 환자(빈맥, 심실세동이 일어날 수 있으며 악화될 수 있다)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 고혈압, 심부전, 부정맥 등 심질환 환자(혈관수축에 의해 급격한 혈압상승이 나타날 수 있다)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 당뇨병 환자(간의 글리코겐 분해를 촉진하고, 인슐린 분비를 억제하므로, 고혈당을 초래할 수 있다)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 동맥경화증 환자(혈관수축작용에 의해 폐색성 혈관장애가 촉진되고, 관동맥ㆍ뇌혈관 등의 수축, 기질적 폐색이 나타날 수 있다)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 소아]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[9) 고령자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[10) 아황산수소나트륨이 함유되어 있으므로 아황산 아나필락시와 같은 알레르기를 일으킬 수 있으며, 일부 감수성 환자에서는 생명을 위협할 정도 또는 이보다 약한 천식발작을 일으킬 수 있다. 일반 사람에서의 아황산감수성에 대한 총괄적인 빈도는 알려지지 않았으나 낮은 것으로 보이며 아황산감수성은 비천식환자보다 천식환자에서 빈번한 것으로 나타났다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 부작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 정신신경계 : 때때로 두통, 수지진전, 졸음이 나타나며, 또한 드물게 어지러움, 신경과민, 불면증, 발한 등의 증상이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 감각기계 : 매우 드물게 인두염, 비충혈이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 소화기계 : 때때로 식욕부진, 소화불량, 구역, 구토 등의 증상이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 순환기계 : 때때로 심계항진, 맥박증가, 혈압변동이 나타나며, 드물게 부정맥, 고혈압 등의 증상이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 호흡기계 : 드물게 기관지 경련, 기침, 기관지염, 천명, 흡입시 기도자극증상 등이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 과민반응 : 매우 드물게 피부 발진, 혈관 부종, 두드러기, 구강인두 부종, 저혈압, 허탈 등의 과민반응이 나타날 수 있으므로 이러한 경우에는 투여를 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 눈 : 때때로 결막, 안검, 눈 주위 등의 과민증상, 결막충혈, 안통 등이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 전신증상 : 폐부종 등 전신성 증상이 발현될 수 있으며, 이러한 경우에는 투여를 중지한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 일반적주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 과도하게 사용을 계속하는 경우 부정맥, 경우에 따라 심정지를 일으킬 우려가 있고 특히 발작 발현시 흡입 투여할 경우에는 과도하게 사용되기 쉬우므로 충분히 주의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 용법ㆍ용량에 맞게 사용하여도 효과가 나타나지 않는 경우에는 이 약이 적당하지 않은 것으로 생각하고 투여를 중지한다. 또한 소아에 투여시 사용법을 정확히 지도하고 경과를 충분히 관찰한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 외국에서 β2-효능약에 의한 중독한 혈중 칼륨치의 저하가 보고되고 있으며, 저하작용은 크산틴유도체, 스테로이드 및 이뇨제의 병용에 의하여 증강하는 것으로 알려져 있으므로 중증 천식 환자에서는 특히 주의한다. 더욱이 저산소혈증은 혈중 칼륨치를 모니터링하는 것이 바람직하다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 고용량의 다른 교감신경 효능약을 투여 받고 있는 환자에게 이 약을 투여시에는 주의해야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 다른 흡입용 β-효능약과 마찬가지로 이 약에 의해 치명적이 될 수 있는 기이성 기관지 경련이 발생될 수 있다. 만약 이러한 증상이 나타나면 즉시 이 약의 투여를 중지하고 다른 치료법으로 대체하여야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 할로탄 등의 할로겐 함유 흡입마취제와 병용투여시 빈맥, 심실세동, 급성 폐부종 발현의 위험성이 증가하므로 병용투여하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 항정신병약(리스페리돈, 페노치아짓계 약물, 뷰틸로페논계 약물 등), 알파 차단제와 병용투여시 이 약의 혈압상승작용의 반전에 의해 저혈압이 나타날 수 있으므로 병용투여하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 이소프레날린 등의 카테콜아민계 약물, 프로테레놀 등의 에피네프린 효능약과 병용투여시 심부정맥이나, 경우에 따라서 심정지를 일으킬 수 있으므로 소생 등의 위급 상황 이외의 경우에는 병용투여하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 프로프라놀롤 등의 비선택성 베타차단제와 병용투여시 혈압상승, 서맥이 발생할 수 있으므로 주의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 인슐린등의 혈당강하제와 병용투여시 혈당강하제의 작용을 감소시킬 수 있으므로 주의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) MAO 저해제나 삼환계 항우울약으로 치료중인 환자에게 이 약을 투여하면 이 약의 혈관계에 대한 작용이 증강될 수 있으므로 특히 주의한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 임부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[태아의 산소결핍을 초래할 수 있으므로 임부 및 임신하고 있을 가능성이 있는 부인에게는 투여하지 않는 것이 바람직하다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 소아에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[소아 등은 전신의 부작용이 나타나기 쉬우므로 초기용량은 소량으로 하고 증상을 관찰하면서 신중히 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 고령자에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[고령자는 이 약물에 대해 감수성이 높으므로 초기용량은 소량으로 하고 증상을 관찰하면서 신중히 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="9. 과량투여시의 처치">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[이 약의 연속투여 중 볼 수 있는 과량투여에 의한 증상은 보통 이 약의 투여를 중지하면 소실된다. 또한 이 약의 과량투여시의 적당한 해독제는 심장 선택성 β-차단제이나 이를 기관지 경련의 병력이 있는 환자에 투여시 주의한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="10. 적용상의 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 주사하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 눈에는 사용하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 흡입이외의 방법으로 사용시 의사 및 약사의 지시에 따라 사용하도록 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 변색에 의한 효력 감퇴는 없지만, 혼탁해진 경우에는 사용하지 않는다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('도란사민캡슐(트라넥삼산)(수출명:트라민캡슐)', '197200091', '제일약품(주)', '1캡슐348밀리그램|트라넥삼산|KP|250.0|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="1. 전신적 섬유소용해항진과 관련있다고 생각되는 출혈 경향 (백혈병, 재생불량성 빈혈, 자반병, 암 등 및 수술 중ㆍ수술 후의 이상 출혈)" />
    <ARTICLE title="2. 국소적 섬유소용해항진과 관련있다고 생각되는 이상출혈 (폐출혈, 성기출혈, 신출혈, 전립선 수술 중ㆍ수술 후의 이상 출혈)" />
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="(캡슐제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[트라넥사민산으로서 보통 성인 1일 750～2000mg을 3～4회에 분할 경구투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(주사제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[트라넥사민산으로서 보통 성인 1일 250～500mg을 1～2회에 분할 정맥 또는 근육주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[수술 중ㆍ수술 후의 출혈 등에는 필요에 따라 1회 500～1000mg을 정맥 또는 500～2500mg을 점적 정맥주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 다음 환자에는 투여하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 트롬빈을 투여 중인 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 중증 신부전 환자 (축적의 위험이 있다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 이 약 및 이 약의 구성성분에 과민반응 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 활동성 혈전색전증 환자]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 신중히 투여할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 혈전이 있는 환자(뇌혈전 심근경색, 혈전정맥염 등) 및 혈전증을 일으킬 우려가 있는 환자(혈전을 안정화할 우려가 있다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 소모성 응고장애가 있는 환자(헤파린등과 병용한다.)(혈전을 안정화할 우려가 있다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 수술 후 침대에 누워있는 환자 및 압박지혈 처치중인 환자 (정맥혈전이 생기기 쉬운 상태이므로, 이 약을 투여하면 혈전이 생길 가능성이 커진다. 침대를 옮기거나, 압박을 중지한 이후 폐색전증이 발생한 예가 보고되었다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 신부전 환자(혈중농도가 상승될 수 있다.)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 이상반응">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 쇽 : 드물게 쇽을 일으킬 수 있으므로 관찰을 충분히하고 이상이 나타날 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 과민반응 : 드물게 가려움, 발진, 홍조등의 과민증상이 나타나는 경우에는 투여를 중지하는 등 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 눈 : 일시적인 색감각이상 등이 나타날 수 있으며, 투여를 중지해야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 비뇨기계 : 상부요로에 대량혈뇨(특히 혈우병환자)가 발생한 환자에서 이 약을 사용시 요관폐쇄가 몇 건 보고되었다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 소화기계 : 때때로 식욕부진, 구역, 구토, 설사, 속쓰림 등의 증상이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 기타 : 드물게 졸음, 두통이 나타날 수 있다. 드물게 혈전색전증이 보고되었다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 일반적 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 유전적으로 혈관신경성부종이 있는 환자에게 장기간 투여 시 정기적인 안검사(시력, 세극등 현미경검사, 안압, 시야) 및 간기능검사를 받도록 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 비정기 월경출혈이 있는 환자는 출혈의 원인이 밝혀질 때까지는 이 약을 사용해서는 안된다. 만약 월경출혈이 이 약에 의해서 적절히 감소되지 않는다면, 다른 치료법을 고려해야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 트롬빈 : 혈전형성을 촉진하는 작용이 있어, 병용시 혈전형성이 증가하므로 병용투여하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 지혈성 장기제제, 헤모코아귤라제와의 대량병용투여에 의해서 혈전형성 작용이 나타날 수 있으므로 병용하는 경우에는 신중하게 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) Batroxobin과의 병용투여에 의해 혈전색전증을 일으킬 수 있으므로 병용하는 경우에는 신중하게 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) Eptacog alfa 등의 응고인자제제와 병용시 구강 등 선용계활성이 강한 부위에서 응고작용이 강화될수 있으므로 신중하게 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 임부 및 수유부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 동물실험을 통한 기형발생 등의 증거는 없지만, 이 약이 태반을 통과하므로 임부에게는 신중하게 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 모체혈중농도의 약 1%의 농도로 모유로 이행되나 영아에게 항섬유소용해등의 영향을 줄것으로 생각되지는 않는다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 소아에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[15세 미만의 월경과다 소아에 대한 사용경험은 부족하다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 고령자에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[일반적으로 고령자는 생리기능이 저하되어 있으므로 감량하는 등 주의한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="9. 과량투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[과량투여시 구역, 구토, 기립성 저혈압 등의 증상이 나타날 수 있다. 구토유발, 위세척, 활성탄투여등의 일반적 처치를 시행한다. 신배설을 촉진시키기 위해 다량의 수액섭취를 유지한다. 혈전형성의 위험이 있는 사람은 항응고제치료를 고려한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="10. 기타">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[개에게 장기간 대량투여 시 망막변화를 일으켰다는 보고가 있다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('에이티피장용정(아데노신트리포스페이트이나트륨삼수화물)', '197200092', '제일약품(주)', '이 약 1정(124mg) 중|아데노신트리포스페이트이나트륨삼수화물|KP|21.96|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(정제)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[다음 질환에 수반하는 여러 증상의 개선 : 심부전, 두부외상 후유증, 조절성 안정피로]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(주사제)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[다음 질환에 수반하는 여러 증상의 개선 :]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[근무력증, 심부전, 만성간염에 있어서의 간기능의 개선, 두부외상 후유증, 조절성 안정피로]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(정제)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[○ 성인 : 삼인산아데노신이나트륨으로서 1회 40∼60mg, 1일 2∼3회 경구투여 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[&nbsp;]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(주사제)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[삼인산아데노신이나트륨으로서 보통 성인 1회 5∼20mg을 1일 1∼2회 피하, 근육 또는 정맥주사 한다. 정맥주사인 경우 포도당주사액 10∼20mL에 혼합한 후 3~5분간에 걸쳐 천천히 주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상의주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 다음 환자에는 투여하지 말 것">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[이 약은 유당을 함유하고 있으므로, 갈락토오스 불내성(galactose intolerance), Lapp 유당분해효소 결핍증(Lapp lactase deficiency) 또는 포도당-갈락토오스 흡수장애(glucose-galactose malabsorption) 등의 유전적인 문제가 있는 환자에게는 투여하면 안 된다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 이상반응">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 소화기계 : 드물게 구역, 식욕부진, 변비, 위통 등의 위장장애 및 구내염이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 순환기계 : 드물게 심계항진이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 과민반응 : 드물게 가려움, 발진이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 중추신경계 : 드물게 두통, 졸음, 기분저하가 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 귀 : 드물게 이명이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 기타 : 드물게 무력감 등이 나타난다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[디피리다몰은 이 약의 분해산물인 아데노신의 혈중농도를 상승시켜, 이 약의 심혈관에 대한 작용을 증강시킨다는 보고가 있으므로 병용투여 시 주의한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 임부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[임신중의 투여에 대한 안전성이 확립되어 있지 않으므로 임부 또는 임신하고 있을 가능성이 있는 여성에는 투여하지 않는 것이 바람직하다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 소아에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[소아에 대한 안전성은 확립되어 있지 않다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 고령자에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[일반적으로 고령자는 생리기능이 저하되어 있으므로 감량하는 등 주의하여 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 적용상의 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 씹어서 복용하지 않도록 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 유발 등으로 갈아서 조제하지 않는다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('동성정로환', '197200125', '동성제약(주)', '1환(200mg)중|크레오소트|KP|44.4|밀리그램|구아이아콜로서11.1mg;1환(200mg)중|황련가루|생규|22.2|밀리그램|베르베린(염화베르베린)으로서 0.93mg;1환(200mg)중|감초가루|생규|22.2|밀리그램|글리시리진산으로서 0.56mg;1환(200mg)중|진피|KP|22.2|밀리그램|헤스페리딘으로서0.89mg;1환(200mg)중|향부자가루|생규|16.6|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[&nbsp;]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[설사, 체함, 묽은 변, 토사]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[&nbsp;]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[성인 및 15세 이상 : 1회 3환]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[소아 : 11세 ∼ 14세 1회 2환, 8세 ∼ 10세 1회 1환]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1일 3회 식후 복용한다. 복용간격은 4시간이상으로 한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 다음과 같은 사람은 이 약을 복용하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 임부 또는 임신하고 있을 가능성이 있는 여성, 수유부]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 지금까지 이 약에 대한 알레르기 증상(발진, 발적, 가려움, 부종 등)을 일으킨 적이 있는 사람]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 만 7세 이하의 소아]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 이 약을 복용하는 동안 다음의 약을 복용하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[다른 지사제]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 다음과 같은 사람은 이 약을 복용하기 전에 의사, 치과의사, 약사와 상의할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 의사 또는 치과의사의 치료를 받고 있는 사람]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 급성의 심한 설사, 발열을 동반하는 설사, 혈변, 점액의 변 또는 복통·복부팽만·구역질 등의 증상을 동반하는 설사가 있는 사람]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 고령자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 본인 또는 부모, 형제·자매가 알레르기 체질인 사람]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 약이나 화장품 등으로 알레르기 증상(발진, 발적, 가려움 등)을 일으킨 적이 있는 사람]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 간장 또는 신장에 질환이 있는 사람]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 다른 약물을 복용하고 있는 사람 : 칼륨함유제제, 감초함유제제, 글리시리진산 혹은 그 염류 함유제제, 루프계 이뇨제 (푸로세미드, 에타크린산) 또는 치아지드계 이뇨제(트리클로르메치아지드)와 함께 복용시 위알도스테론증이나 저칼륨혈증으로 인하여 근육병증이 나타나기 쉬우므로 신중히 복용할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 혈압이 높은 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[9) 심장애 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[10) 부종이 있는 환자]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 다음과 같은 경우 이 약의 복용을 즉각 중지하고 의사, 치과의사, 약사와 상의할 것. 상담시 이 첨부문서를 소지할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약을 복용함으로써 다음 증상이 나타날 경우 : 알레르기 증상(발진, 발적, 가려움, 부종 등), 식욕부진, 위부불쾌감, 어지럼, 두통, 간기능장애(전신의 노곤함, 황달 등)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 수일간 (4 ∼ 5일간) 복용하여도 증상의 개선이 없을 경우]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 위알도스테론증 : 요량이 감소하거나 얼굴과 손발이 붓고, 눈꺼풀이 무거워지고, 손이 굳어지고, 혈압이 높아지거나 두통 등(1일 최대 복용량이 감초로서 1g 이상인 제제는 장기간 계속하여 복용할 경우 저칼륨혈증, 혈압상승, 나트륨 체액의 저류, 부종, 체중증가 등의 위알도스테론증이 나타날 수 있으므로, 관찰(혈청칼륨치의 측정)을 충분히 하고 이상이 확인되는 경우 복용을 중지할 것)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 근병증 : 저칼륨혈증의 결과로서 근병증이 나타날 수 있으므로, 관찰을 충분히 하고 무력감, 사지경련, 마비 등의 이상이 확인되는 경우]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 기타 이 약의 복용시 주의할 사항">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 정해진 용법·용량을 잘 지킬 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 어린이에게 복용시킬 경우에는 보호자의 지도 감독하에 복용시킬 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 1주 이상 계속하여 복용하지 말 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 이 약을 정해진 효능 이외의 목적으로 사용하지 말 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 이 약이 실수로 눈에 들어간 경우에는 바로 물로 씻고, 전문가의 진찰을 받을 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 이 약이 실수로 피부에 묻은 경우에는 비누 또는 뜨거운 물을 이용하여 잘 씻어낼 것.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 저장상의 주의사항">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 어린이의 손이 닿지 않는 곳에 보관할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 의약품을 원래 용기에서 꺼내어 다른 용기에 보관하는 것은 의약품 오용에 따른 사고 발생이나 의약품 품질 저하의 원인이 될 수 있으므로 원래의 용기에 넣고 꼭 닫아 보관할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 직사광선을 피하고 되도록 습기가 적은 서늘한 곳에 보관할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 이 약에 다른 약제 등을 절대 섞이게 하지 말 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 이 약에는 특이한 “냄새”가 있는 성분(크레오소트)이 함유되어 있어 사용 후에는 뚜껑을 완전히 봉하여 보관할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 이 약의 “냄새”가 의류 등에 배인 경우에는 되는대로 빨리 일반적인 세탁을 하여 바람이 잘 통하는 곳에서 건조할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 사용기한이 지난 약은 사용하지 말 것.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('후릭스정(푸로세미드)', '197200160', '일양약품(주)', '이 약 1정(165.26밀리그램) 중|푸로세미드|KP|40|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="(경구 : 정제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[고혈압(본태성, 신성 등), 심성부종(울혈성심부전), 신성부종, 간성부종(복수), 말초혈관성부종]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(주사제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[고혈압(본태성, 신성 등), 심성부종(울혈성심부전), 신성부종, 간성부종(복수), 급성폐부종]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="(경구 : 정제)">
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[1. 성인 : 푸로세미드로서 1일 1회 20-80㎎을 연일 또는 격일 경구투여한다. 충분한 효과가 나타나지 않을 경우 6-8시간마다 20-40㎎씩 증량하여 1일 1-2회 투여한다. 심한 경우 1일 600㎎까지 투여할 수 있다. 고혈압에는 1일 80㎎을 2회 분할투여한다. 충분한 효과가 나타나지 않을 경우에는 다른 혈압강하제(레세르핀등)와 병용투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[2. 소아 : 이 약으로서 체중 ㎏당 2㎎을 투여한다. 충분한 효과가 나타나지 않을 경우에는 6-8시간마다 체중 ㎏당 1-2㎎을 증량한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(주사제)">
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[1. 성인 : 푸로세미드로서 성인 1일 1회 20-40㎎을 정맥 또는 근육에 주사한다. 충분한 효과가 나타나지 않을 경우에는 2시간마다 20㎎씩 증량하여 1일 1-2회 주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[2. 소아 : 체중 ㎏당 1㎎을 정맥 또는 근육 주사한다. 2시간마다 체중 ㎏당 1㎎씩 증량하여 주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상의주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 경고">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[이 약은 강력한 이뇨제로 과량을 투여할 경우 심각한 수분 및 전해질 결핍이 나타날 수 있다. 그러므로 이 약을 투여하는 경우 정기적인 검사가 필요하며, 개별 환자의 필요에 따라 용량을 적절하게 조절하여야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 투여하지 말 것">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약 및 이 약의 구성성분에 과민증이 있는 환자.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[설폰아미드계 약물(예, 설폰아미드계 항생제, 설폰요소제)에 알러지가 있는 환자는 이 약과 교차 과민반응이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 저혈량증 또는 탈수 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 무뇨환자 또는 이 약에 반응하지 않는 무뇨성 신부전 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 신ㆍ간독성물질 중독 결과에 의한 신부전 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 중증의 저칼륨혈증 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 중증의 저나트륨혈증 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 중증의 간장애 환자 (간성 뇌병증과 관련된 혼수상태 또는 전혼수상태의 환자)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 임산부]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[9) 수유부]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[10) 테르페나딘 또는 아스테미졸을 투여받고 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[11) 이 약은 유당을 함유하고 있으므로, 갈락토오스 불내성(galactose intolerance), Lapp 유당분해효 소 결핍증(Lapp lactase deficiency) 또는 포도당-갈락토오스 흡수장애(glucose-galactose malabsorption) 등의 유전적인 문제가 있는 환자에게는 투여하면 안 된다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 다음 환자에는 신중히 투여할 것">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 뇨배출이 부분적으로 폐쇄되어 있는 환자(예. 방광배출질환, 전립선비대 혹은 요도 좁아짐)에서 뇨량 증가는 불편을 유발시킬 수 있다. 이런 환자는 특히 치료초기에 주의 깊은 모니터링이 요구된다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약의 투여는 정기적인 의학적 관리를 요한다. 다음 환자들은 특별히 주의 깊은 모니터링이 필요하다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 저혈압 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 심한 관상동맥경화증 또는 뇌동맥경화증 환자(급격한 이뇨가 나타날 경우 급속한 혈장량감소ㆍ혈액농축을 일으켜 혈전색전증이 나타날 수 있다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 심각한 혈압저하의 특정 위험이 있는 환자. 예를 들어 심각한 관상동맥 혹은 뇌 혈관 협착]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 잠재적인 혹은 밝혀진 당뇨병 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 통풍 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 진행성 간경변 환자(간성 혼수가 나타날 수 있다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 간질환ㆍ간기능장애 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 중증의 신장애 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 간신증후군 환자. 예를 들어 심각한 간 질환과 관련된 신장 기능 부전]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 저단백혈증 환자. 신증후군과 관련될 수 있다. (이 약의 효과가 약화되고 내이독성 이상반응이 더 심해질 수 있으므로, 주의 깊은 용량 조절이 필요하다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 조산아 (신석회증 또는 신결석증이 나타날 수 있으므로 신기능을 반드시 모니터링해야 하고 신장 초음파촬영을 실시한다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 염제한요법 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 이 약의 투여 기간 동안 정기적인 혈중 나트륨, 칼륨, 크레아티닌, 혈당 모니터링이 권장된다. 전해질 불균형이나 심각한 체액손실이 있는 경우(예, 구토, 설사, 심한 발한)에는 특히 면밀한 모니터링이 필요하다. 저혈량증이나 탈수 그리고 심각한 전해질이나 산-염기 장애는 반드시 교정되어야 하며, 일시적으로 약물중단이 필요할 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 치매 노인에 대한 리스페리돈의 위약-대조 임상시험에서, 리스페리돈 단독투여 시 (3.1%;평균 84세, 70-96세) 혹은, 푸로세미드 단독투여 시(4.1%;평균 80세, 67-90세)보다 푸로세미드와 리스페리돈을 병용투여 했을 때 사망률이 높게 나타났다.(7.3%;평균 89세, 75-97세) 리스페리돈과 다른 이뇨제(주로 저용량의 티아지드계 이뇨제)의 병용에서는 유사한 결과가 나타나지 않았다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[이를 설명하는 병리학적 기전은 확인되지 않았고 사망의 원인도 일관되게 나타나지 않았다. 그럼에도 주의를 기울여야 하고 이런 병용요법을 실시하기 전에 위험성 대비 유익성이 고려되어야 한다. 다른 이뇨제와 리스페리돈 병용 시에는 사망률이 증가하지 않았다. 치료와 상관없이, 탈수는 노인 환자에서 전반적인 사망의 위험 요인이므로, 치매 노인 환자의 경우 투여를 피해야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 전신 홍반 루프스 질환 병력이 있는 환자의 경우 전신 홍반 루프스의 악화 혹은 활성화가 있을 수 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 이상반응">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 대사 및 영양]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 저나트륨혈증, 저염소혈증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 저칼륨혈증, 저칼슘혈증, 저마그네슘혈증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 전해질장애(증후성 포함)와 대사성 알칼리증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 특히 고령자에서 탈수와 저혈량증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 일시적인 혈중 크레아티닌과 요소 증가]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 혈중 콜레스테롤과 중성지방 증가]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 혈중 요산의 증가와 통풍 발작]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 당내성 손상. 잠재적 당뇨병이 확실한 당뇨병으로 나타날 수 있음.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- Pseudo-Bartter씨 증후군]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 심혈관계]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 부정맥]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 기립성 저혈압을 포함한 저혈압]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 혈전증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 혈관염]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 신장 및 비뇨기계]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 급성뇨저류 (뇨배출이 부분적으로 폐색된 환자의 경우)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 간질신장염]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 조산아의 경우 신장석회/신장결석]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- BUN과 혈중크레아티닌의 증가]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 소변량 증가]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 뇨중 나트륨 및 염소 증가]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 신부전]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 위장관계]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 구갈, 구역, 구토, 설사]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 급성 췌장염]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 식욕부진]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 간 담도계]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 간내 담즙정체, 간 내 트랜스아미나제 증가]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 황달]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 눈]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 시각 이상]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 귀 및 미로]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 청각장애와 이명(주로 일시적이나 특히 신장질환/ 저단백혈증 (예, 신증후군) 환자나 정맥투여로 약물이 너무 빠르게 투여되었을 경우)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 난청]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 피부 및 피하조직]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 가려움, 두드러기, 발진,]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 물집피부염, 다형홍반, 박탈피부염]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 스티븐스-존슨증후군, 독성표피괴사용해]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 유사천포창, 급성전신발진농포증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 자색반, 광과민반응]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 호산구증가증과 전신증상을 동반한 약물성발진 (Drug Rash with Eosinophilia and Systemic Symptoms, DRESS)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 태선모양반응]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[9) 면역계]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 아나필락시스 반응, 아나필락시양 반응 (예.쇼크)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 전신 홍반 루프스 악화 또는 활성화]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[10) 신경계]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 감각이상]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 간성뇌병증(간세포의 기능저하 환자)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 집중력 및 반응도 저하]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 혼돈, 현기증, 두중감, 어지러움, 졸림, 쇠약, 실신 또는 의식소실]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[11) 혈액 및 림프계]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 혈소판감소증, 백혈구감소증, 과립구감소, 무과립구증, 호산구 증가증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 빈혈, 재생불량성 빈혈, 용혈성 빈혈]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 혈액농축]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[12) 근골격계]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 근육경련, 강직, 근무력]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 횡문근융해증이 보고된 바 있으며, 이는 종종 심한 저칼륨혈증에서 발생 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[13) 선천성 내지 유전성]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 조산아에게 생후 첫 주에 투여 시 타고난 동맥관열림증의 유지 위험이 증가]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[14) 투여 부위 및 일반]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 근육내 주사 시 통증 등의 국소반응 - 발열 - 무력감, 권태감]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 일반적 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약 투여시 혈청내 나트륨, 칼륨 및 크레아티닌을 정기적으로 모니터링하여야 하며, 뇨배출이 원활히 이루어져야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약의 이뇨작용은 급격히 나타날 수 있으므로 전해질평형실조, 탈수에 특히 주의하고 저용량에서 투여를 시작하여 천천히 증량한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 이 약을 연용 하는 경우에는 전해질평형실조가 나타날 수 있으므로 정기적으로 검사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 혈압강하작용에 의한 어지러움 등 이 약의 이상반응에 의해 집중하거나 반응하는 능력이 손상될 수 있으므로 고소작업이나 자동차운전 등 위험이 수반되는 기계조작을 하지 않도록 주의한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6.1 음식]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[이 약은 빈 속에 복용할 것이 권장된다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6.2 약물]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 병용투여가 권장되지 않는 약물]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[① 이 약과 테르페나딘의 병용투여시 QT연장, 심실성 부정맥이 발생되었다는 보고가 있으므로 병용투여 하지 않는다. 또한 이 약과 아스테미졸의 병용투여시 QT 연장, 심실성 부정맥을 일으킬 수 있으므로 병용투여 하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[② 클로랄하이드레이트를 투약한 후 24시간 이내에 이 약을 정맥 주사하는 경우 드물게 홍조, 갑작스러운 발한증상, 안절부절증, 오심, 혈압상승 또는 빈맥이 나타날 수 있으므로 병용투여는 권장되지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[③ 이 약은 아미노글리코사이드와 다른 이독성 약물의 이독성 유발을 증가시킬 수 있고 비가역적인 손상을 줄 수 있으므로 의학적인 이유로 꼭 필요할 때만 이 약과 병용한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 병용투여 시 주의를 요하는 약물]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[① 이 약은 시스플라틴과 병용투여시 청각장애 등 이독성이 증가될 수 있다. 게다가 시스플라틴을 투여하는 동안에 강제적인 배뇨를 위해 이 약을 투여할 경우 저용량(예, 정상적인 신기능을 가진 환자에게 이 약 40mg에 해당하는 용량)을 사용하지 않고, 환자가 신체내로 유입되는 수분의 양이 신체에서 빠져나가는 양보다 많은 상태(positive fluid balance)일 때에는 시스플라틴의 신독성이 증가될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[② 수크랄페이트는 소장에서 이 약의 흡수를 저하시켜 그 작용을 약화시킬 수 있으므로 수크랄페이트와 함께 병용투여하는 경우에는 반드시 2시간 이상의 간격을 두어야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[③ 이 약은 리튬염의 배설을 감소시켜 혈청내 리튬수치를 증가시킬 수 있고 그로인해 심독성 및 신경독성 같은 리튬 독성을 증가시킬 수 있다. 그러므로 이 약과 리튬염을 병용투여하는 경우에는 리튬수치를 주의깊게 모니터링해야 한다]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[④ 이뇨제를 투여하고 있는 환자는, 특히 ACE 저해제 또는 안지오텐신 II 수용체 길항제를 처음 투여받거나 처음으로 용량을 증가시키는 경우, 심각한 저혈압 및 신부전을 포함한 신기능 악화가 나타날 수 있다. 그러므로 ACE 저해제 또는 안지오텐신 II 수용체 길항제를 처음 투여받거나 처음으로 용량을 증가시키기 전 3일 동안은 이 약의 투여를 중단하거나 적어도 투여량을 감소시켜야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[⑤ 리스페리돈 : 이 약 또는 그 복합제 또는 다른 이뇨제와 병용 투여 시 주의를 기울이고 이런 병용요법을 실시하기 전에 위험성 대비 유익성에 대해 고려되어야 한다(3. 다음 환자에는 신중히 투여할 것 항 참고).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[⑥ 레보티록신: 푸로세미드 고용량은 갑상선 호르몬이 수송 단백질에 결합하는 것을 저해할 수 있어 유리된 갑상선 호르몬을 일시적으로 증가 시켜 전체 갑상선 호르몬 양을 감소시킨다. 갑상선 호르몬 수치를 모니터해야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 병용투여 시 고려할 사항]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[① 아세틸살리실산을 포함하여 비스테로이드성 소염진통제를 이 약과 병용투여시 이 약의 효과를 감소시킬 수 있다. 탈수증이나 혈액량감소증이 있는 환자에서 비스테로이드성 소염진통제는 급성 신부전을 일으킬 수 있다. 살리실산의 독성이 이 약에 의해 증가될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[② 페니토인과 병용투여에 의해 이 약의 효과가 약화될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[③ 코르티코스테로이드, 카르베녹솔론, 암포테리신 B, 많은 양의 감초, 장기간의 하제 복용은 저칼륨혈증의 위험을 높일 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[④ 이 약에 의한 전해질장애는(예. 저칼륨혈증, 저마그네슘혈증) 특정한 다른 약물의 독성을 증가시킬 수 있다.(예. 디기탈리스 제제, QT 간격 연장을 일으키는 약물들)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[⑤ 다른 혈압강하제, 이뇨제, 혈압을 저하시킬 수 있는 약물을 투여받고 있는 환자에게 이 약을 병용투여 하는 경우에는 심각한 혈압저하를 일으킬 수 있음을 예측하여야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[⑥ 이 약과 같이 주로 세뇨관분비를 하는 프로베니시드, 메토트렉세이트 및 기타 약물은 이 약의 효과를 감소시킬 수 있다. 반대로 이 약은 이러한 약물의 신장 배설을 감소시킬 수 있다. 고용량 투여(특히 이 약과 다른 약물 모두 고용량 투여)하는 경우 혈청농도를 증가시켜 이 약 또는 병용약물의 이상반응을 증가시킬 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[⑦ 혈당강하제와 혈압증가 교감신경유사작용약물(예. 에피네프린, 노르에피네프린)의 효과가 감소할 수 있다. 큐라레 계열의 근이완제나 테오필린의 효과가 증가될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[⑧ 세팔로스포린계, 아미노글리코사이드계 항생물질 등을 포함하는 신독성약물의 신장에 대한 유해작용이 이 약에 의해 증가될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[⑨ 이 약과 특정 세팔로스포린 제제의 고용량을 병용하는 환자에서 신기능 부전이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[⑩ 이 약과 사이클로스포린 A의 병용투여 시 이 약에 의해 유도된 고요산혈증에 의해 이차적으로 생성되는 통풍성 관절염의 위험뿐 아니라, 사이클로스포린에 의한 신장 요산염 배설 손상에 대한 위험이 증가한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[⑪ 방사성 조영제 투여 전 정맥으로 수액보충만을 받은 경우에 비해 방사성 조영제로 인한 신장병증의 위험이 있는 환자들에게 이 약을 투여한 경우, 방사성 조영제 투여 후의 신기능 악화가 더 흔하게 나타났다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[⑫ 메트포르민에 의한 젖산혈증의 위험이 있으므로 혈중 크레아티닌치가 남성 1.5ml/dl이상, 여성 1.2ml/dl이상일 경우에는 메트포르민과 이 약을 병용투여하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[⑬ 이뇨제에 의한 체액 감소환자에게 요오드 함유물질을 병용투여하는 경우에는 이 약의 투여 전에 수분을 공급한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[⑭ 제산제(인산알루미늄)와 병용투여하는 경우에는 이뇨제의 흡수가 감소될 수 있으므로 이뇨제와 투여간격을 두고 제산제를 복용한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7. 임부 및 수유부에 대한 투여]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약은 태반을 통과한다. 의학적으로 꼭 필요한 경우를 제외하고 임신 중에 투여해서는 안 된다. 임신 중 투여 시에는 태아에 대한 모니터링이 요구된다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약은 모유로 분비되고 수유를 방해할 수 있다. 이 약을 복용 시 모유 수유해서는 안된다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8. 소아에 대한 투여]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 조산아에서는 신석회화증 또는 신결석증이 나타날 수 있으므로 신중히 투여한다. 태어난지 일주일 이내의 미숙아에게 이 약을 투여하면 동맥관열림증의 위험이 증가될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 영아에서는 전해질평형실조가 나타날 수 있으므로 신중히 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[9. 고령자에 대한 투여]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[고령자에는 다음 사항을 주의하여 저용량에서 투여를 시작하는 등 환자의 상태를 관찰하면서 신중히 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 고령자에서의 급격한 이뇨는 혈장량의 감소를 초래하므로 탈수, 저혈압 등에 의한 기립성조절장애, 어지러움, 실신 등이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 특히 심질환 환자 중 부종이 있는 고령자에서의 급격한 이뇨는 급속한 혈장량감소ㆍ혈액농축을 초래하여 뇌경색 등의 혈전색전증이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 일반적으로 고령자에서의 과도한 혈압강하는 바람직하지 않다.(뇌경색이 나타날 수 있다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 고령자에서는 저나트륨혈증, 저칼륨혈증이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[10. 과량투여시의 처치]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 징후 및 증상]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[급성 또는 만성 과량투여시의 임상적 징후로는 일차적으로 전해질과 체액손실 정도에 따라서 혈액량 감소, 탈수, 혈액농축, 부정맥(방실 차단 및 심실 세동 포함) 등이 나타난다. 또한 이러한 장애의 증상으로는 쇼크로 진행될 수 있는 심각한 저혈압, 급성신부전, 혈전증, 섬망증, 이완마비, 무감동 및 혼돈 등이 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 처치]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[특별한 해독제는 알려져 있지 않다. 만약 이 약의 과량복용이 막 발생했다면, 즉시 위 세척을 하거나 활성탄 등을 이용하여 흡수를 감소시키는 등의 조치를 통하여 이 약의 주성분이 더 이상 전신으로 흡수되는 것을 막을 수 있다. 전해질이나 체액의 평형 실조는 반드시 교정되어야 한다. 전해질이나 체액손실로 인한 심각한 합병증 등을 치료하고 예방하는 것과 함께, 이러한 교정 작업은 집중적인 의학적인 모니터링과 치료수단을 필요로 할 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[11. 적용상의 주의]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[야간에 휴식이 필요한 환자는 야간에 배뇨를 피하기 위하여 이 약을 오전중에 투여하는 것이 바람직하다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[12. 보관 및 취급상의 주의사항]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 어린이의 손이 닿지 않는 곳에 보관할 것]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 다른 용기에 바꾸어 넣는 것은 사고원인이 되거나 품질 유지면에서 바람직하지 않으므로 이를 주의할 것]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('천우조고약', '197200165', '천우신약(주)', '1그램 중|황기|KP|5|밀리그램|;1그램 중|금은화|KP|5|밀리그램|;1그램 중|현삼|KP|5|밀리그램|;1그램 중|연단|별규|250|밀리그램|;1그램 중|황납|KP|5|밀리그램|;1그램 중|행인|KP|5|밀리그램|;1그램 중|창출|KP|2|밀리그램|;1그램 중|대황|KP|4|밀리그램|;1그램 중 1그람중|황|KP|10|밀리그램|;1그램 중 1그람중|진피|KP|10|밀리그램|;1그램 중 1그람중|백당|KP|180|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[&nbsp;]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[종기, 고름집]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[&nbsp;]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[매일 1회 환부의 크기에 따라 고약을 적당히 붙이되 곪기 전에는 고약을 환부의 2배 정도로 늘려서 매일 1회 갈아 붙이고, 곪은 후에는 먼저 발근고를 녹두알 크기로 떼어 환부중심에 붙이고, 그 위에 고약을 환부의 2배로 늘려서 매일 1회 갈아 붙임]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 다음과 같은 사람(부위)은 이 약을 사용하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 유ㆍ소아]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 임부 또는 임신하고 있을 가능성이 있는 여성 및 수유부]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 이 약 및 이 약의 구성성분에 과민반응 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 눈 또는 눈주위]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 기타 이 약의 사용시 주의할 사항">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 1주 정도 사용 후에도 증상의 개선이 보이지 않을 경우에는 사용을 중지하고 약사 또는 의사와 상의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 장기간ㆍ대량 흡수되는 경우에는 납중독을 일으킬 수 있다는 보고가 있으므로 장기간ㆍ대량 사용하지 말 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 과민반응이 나타나는 경우에는 사용을 중지할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 곪은 부위는 침투가 쉬우며 납의 흡수가 우려되므로 주의하여 사용할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 어린이의 손이 닿지 않는 곳에 보관할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 이 약을 사용중에는 닭고기, 돼지고기, 술, 청어, 고등어, 기타 지방성이 많은 음식물을 피할 것.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
