INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('코바마이드정"아세아"', '197200187', '서울약품(주)', '1정중|5,6-디메칠-벤지미다졸릴-5-데옥시-아데노실코바마이드|별규|1000|마이크로그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="1. 주효능효과">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(1) 비타민 B12 결핍증(악성빈혈)의 예방 및 치료]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(2) 비타민 B12의 수요가 증대하여 음식중에서 섭취가 불충분한 경우의 보급(소모성질환, 갑상선기능항진증 임신부, 수유부등)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(3) 거적아구성빈혈(주사)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(4) 광절열두조충증(주사)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(5) 악성빈혈에 수반된 신경장해(주사)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(6) 흡수부전증후군(스프루등)(주사)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 질환에도 사용할 수 있다.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(1) 거적아구성빈혈(경구)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(2) 광절열두조충증(경구)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(3) 악성빈혈에 수반된 신경장해(경구)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(4) 흡수부전증후군(스프루등)(경구)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(5) 비타민 B12의 결핍 또는 대사 장해가 관여한다고 생각되는 경우의]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[①영양성 및 임신성빈혈]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[②위 또는 회장절제 후의 빈혈]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[③간장해에 수반된 빈혈]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[④방사선에 의한 백혈구감소즈]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[⑤신경통]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[⑥말초신경염, 말초신경마비]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[⑦근육통, 관절통]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[⑧중추신경장해(척수염,변성질환 등)]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[경구 : 아데노실비12로서 보통 성인 1일 1,500 마이크로그람까지 1~3회 분할 경우 투여한다. 연령,증상에 따라 적절히 증감한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[주사 : 아데노실 비12로서 보통 성인 1일 500~1000마이크로그람까지 근육내 주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 지시된 용법.용량의 범위내에서는 안전하게 사용할 수 있으나 유효하리라고 추정되는 적응증에 대해서는 효과가 없는데 월여에 걸쳐 장기투여할 필요는 없다." />
    <ARTICLE title="2. 거적아구성 빈혈, 광절열두조충증, 악성빈혈에 수반된 신경장해. 흡수부전증후군(스프루 등) 위 및 회장절제후의 빈혈에 대한 사용은 경구 투여시 흡수가 좋지 못하므로 가능한 주사투여가 바람직 하다." />
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('아펙스정(수출용)(수출명:DIMESTABLET)', '197200208', '영일제약(주)', '1정 중 774밀리그램|시메치콘|USP|14.0|밀리그램|;1정 중 774밀리그램|염산메토클로프라미드|KP|6.0|밀리그램|메토클로프라미드로서 4.6 밀리그램;1정 중 774밀리그램|다이제트100|별규|10.0|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[식욕감퇴(식욕부진), 위부팽만감, 소화불량, 과식, 식체(위체),]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[구역, 구토, 소화촉진]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[이 약은 1정 중 메토클로프라미드로써 4.6mg을 함유하고 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[비록 구토 또는 용량 거부가 있었더라도, 투여간격은 적어도 6시간 이상이 권장된다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[1. 성인 : 성인 1회 1정 1일 3회 식후에 복용한다. 일일최대권장용량은 메토클로프라미드로써 30mg 또는 체중 kg 당 0.5mg이며, 최대 권장 치료기간은 5일이다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[2. 소아 : 권장용량은 메토클로프라미드로써 체중 kg 당 0.1～0.15mg이며, 일일 3회까지 반복하여 투여한다. 24시간 내 최대용량은 메토클로프라미드로써 체중 kg당 0.5mg이며, 이 약의 최대 권장 치료기간은 5일이다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(용량표)]]></PARAGRAPH>
      <PARAGRAPH tagName="table" textIndent="" marginLeft=""><![CDATA[<tbody> 
 <tr> 
  <td> <p>연령</p> </td> 
  <td> <p>체중</p> </td> 
  <td> <p>1회투여량</p> </td> 
  <td> <p>투여빈도</p> </td> 
 </tr> 
 <tr> 
  <td> <p>1-3세</p> </td> 
  <td> <p>10-14kg</p> </td> 
  <td> <p>1mg</p> </td> 
  <td> <p>일일 3회까지</p> </td> 
 </tr> 
 <tr> 
  <td> <p>3-5세</p> </td> 
  <td> <p>15-19kg</p> </td> 
  <td> <p>2mg</p> </td> 
  <td> <p>일일 3회까지</p> </td> 
 </tr> 
 <tr> 
  <td> <p>5-9세</p> </td> 
  <td> <p>20-29kg</p> </td> 
  <td> <p>2.5mg</p> </td> 
  <td> <p>일일 3회까지</p> </td> 
 </tr> 
 <tr> 
  <td> <p>9-18세</p> </td> 
  <td> <p>30-60kg</p> </td> 
  <td> <p>5mg</p> </td> 
  <td> <p>일일 3회까지</p> </td> 
 </tr> 
 <tr> 
  <td> <p>15-18세</p> </td> 
  <td> <p>60kg 초과</p> </td> 
  <td> <p>10mg</p> </td> 
  <td> <p>일일 3회까지</p> </td> 
 </tr> 
</tbody>]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[이 약은 1세 미만 소아에게 금기이다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[3. 노인 : 신장 및 간 기능, 전체적인 쇠약도에 근거하여 용량감소를 고려해야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[4. 신장애 환자: 말기 신장질환자(크레아티닌 청소율 ≤ 15ml/min)는 일일 용량을 75%까지 감소해야 한다. 중등도에서 중증 신장애 환자(크레아티닌 청소율 15～60 ml/min)는 용량을 50%까지 감소해야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[5. 간장애 환자 : 중증 간장애 환자는 용량을 50%까지 감소해야 한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 경고">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[지발성운동장애 : 메토클로프라미드의 장기 또는 고용량 사용은 해당 의약품 사용을 중단해도 지발성운동장애(tardive dyskinesia)의 발생 위험이 있다. 이는 복용기간, 복용횟수와 직접적 관련이 있으며 노인, 특히 여성 노인과 장기 사용 환자에게 위험하므로 메토클로프라미드 제제를 3개월 이상 장기 사용하지 않는 것이 바람직하다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 투여하지 말 것">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 친크롬성세포종 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 갈색 세포종의 의심이 있는 환자(급격한 승압발작을 일으킬 우려가 있다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 이 약에 과민성이나 불내성을 나타내는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 위장관 출혈, 기계적폐색증, 천공 등 위장관 운동 자극이 위험할 때]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 신경이완제 또는 메토클로프라미드 유발 지연성 이상운동증 병력 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 간질 (발작 빈도 및 강도 증가) 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 파킨슨병 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 레보도파 또는 도파민효능제 병용투여 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[9) 잘 알려진 메토클로프라미드에 의한 메트해모글로빈혈증 또는 NADH 시토크롬 b5 결핍 병력 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[10) 1세 미만 소아 (추체외로장애 위험성 증가)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 다음 환자에는 신중히 투여할 것">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 1～18세 소아(추체외로증상이 발현되기 쉽기 때문에 과량 투여하지 않도록 주의한다. 특히 탈수상태, 발열시 등에는 주의한다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 고령자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 신장애 환자]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 부작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 말린증후군(악성신경마비증후군) : 운동마비, 심한 근육강직, 연하곤란, 실어증, 빈맥, 혈압 변화등이 나타나며 발열이 나타나는 경우에는 투여를 중지하고 체냉각과 수분 급등의 전신관리와 함께 적절한 처치를 한다. 이 증상의 발현시에는 백혈구 증가, 혈청 CPK치 상승이 자주 나타나고 미오글로빈뇨증을 수반하는 신기능 저하가 나타날 수 있다. 또한 고열이 지속되고 의식 장애, 호흡곤란, 순환허탈과 탈수 증상, 급성신부전으로 발전해서 사망했다는 보고가 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 내분비계 : 드물게 간뇌의 내분비기능조절이상(성선자극호르몬 및 프로락틴 분비 이상)에 유래한다고 추정되는 무월경, 지속성 유즙 누출 및 여성형 유방 등이 나타나는 경우가 있으므로 충분히 관찰하고 이상이 인정되는 경우 투여를 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 추체외로증상 : 드물게 수지진전, 근경직, 경·안부의 연축, 안구회전발작, 초조감 등의 증상이 발현되는 경우가 있으므로 이러한 증상이 나타나는 경우에는 투여를 중지한다. 또한 이러한 증상이 강한 경우에는 항파킨슨제 투여 등의 적절한 처치를 한다. 또, 장기투여에 의해서 드물게 입주의 등의 불수의운동이 나타나고 투여중지 후 에도 지속되는 경우가 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 소화기계 : 때때로 위의 긴장 증가, 복통, 설사, 변비통의 증상이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 순환기계 : 드물게 혈압강하, 빈맥, 부정맥 등의 증상이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 정신신경계 : 때때로 두통, 두중 드물게 훙분, 불안감 등의 증상이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 기타 : 때때로 권태감 또한 드물게 발진, 부종이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 그 외 이상반응 : 이상반응은 기관계별로 나타내었으며, 빈도는 다음과 같이 정의한다.: 매우 흔하게 (≥1/10), 흔하게 (≥1/100, &lt;1/10). 흔하지 않게 (≥1/1000, &lt;1/100), 드물게 (≥1/10000, &lt;1/1000), 매우 드물게(&lt;1/10000), 알 수 없음 (이용가능한 자료로부터 예측할 수 없음)]]></PARAGRAPH>
      <PARAGRAPH tagName="table" textIndent="" marginLeft=""><![CDATA[<tbody> 
 <tr> 
  <td> <p>기관</p> </td> 
  <td> <p>빈도</p> </td> 
  <td> <p>이상반응</p> </td> 
 </tr> 
 <tr> 
  <td> <p>혈액 및 림프계 장애</p> </td> 
  <td> <p>알 수 없음</p> </td> 
  <td> <p>특히 신생아에서 NADH 시토크롬 b5 환원제와 관련될 수 있는 메트헤모글로빈혈증</p> <p>주로 황을 방출하는 약물을 고용량으로 병용투여했을 때, 황혈색소혈증</p> </td> 
 </tr> 
 <tr> 
  <td rowspan="2"> <p>심장 장애</p> </td> 
  <td> <p>흔하지 않게</p> </td> 
  <td> <p>특히 정맥투여했을 때, 서맥</p> </td> 
 </tr> 
 <tr> 
  <td> <p>알 수 없음</p> </td> 
  <td> <p>주사제 투여 후 짧게 발생하며, 그 다음에 서맥이 발생할 수 있는 심장정지;</p> <p>특히 정맥주사 투여시 방실차단, 동정지;</p> <p>심전도 QT 연장; 다형성 심실 빈맥(Torsade de Pointes)</p> </td> 
 </tr> 
 <tr> 
  <td rowspan="3"> <p>내분비계 장애*</p> </td> 
  <td> <p>흔하지 않게</p> </td> 
