INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('아나바시주(아데노실코발라민주)', '197600076', '서울약품공업(주)', '1000마이크로그람중|코바마미드(코엔자임비12)|별규|1000|마이크로그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="1. 주효능 효과 :">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 비타민 비12 결핍증(악성빈혈)의 예방 및 치료]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 비타민 비12의 수요가 증대하여 음식중에서 섭취가 불충분한 경우의 보급]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[(소모성질환, 갑상선 기능항진증, 임산부, 수유부등)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 거적아구성빈혈]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 광절열두조충증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 악성빈혈에 수반된 신경장애]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 흡수부전증후군(스프루등)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 질환에도 사용할 수 있다.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 비타민 비12의 결핍 또는 대사장해가 관여한다고 생각되는 경우의]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[가) 영양성 및 임신성 빈혈]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[나) 위 또는 회장절제 후의 빈혈]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[다) 간장해에 수반된 질환]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[라) 방사선에 의한 백혈구 감소증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[마) 신경통]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[바) 말초신경염, 말초신경마비]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[사) 근육통, 관절통]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[아) 중추신경장애(척수염, 변성질환등)]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[아데노실비12로서 보통 성인 1일 500-1000마이크로그람까지 근육내주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 지시된 용법.용량의 범위내에서는 안전하게 사용할 수 있으나 유효하리라고 추정되는 적응증에 대해서는">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[효과가 없는 데 월여에 걸쳐 장기 투여할 필요는 없다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 거적아구성빈혈, 광절열두조충증, 악성빈혈에 수반된 신경장해, 흡수부전증후군(스프루등), 위 및">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[회장절제후의 빈혈에 대한 사용은 경구 투여시 흡수가 좋지 못하므로 가능한 주사 투여가 바람직하다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('케스조린주1그람(주사용세파졸린나트륨)', '197600077', '서울약품공업(주)', '1그람중|세파졸린나트륨|항기|1|그램|', 'nan', 'nan', 'nan');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('에이프로젠아미카프정(아미노카프론산)', '197600100', '(주)에이프로젠바이오로직스', '550밀리그램|아미노카프론산|KP|500|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="1. 전신적 섬유소용해항진과 관련있다고 생각되는 출혈 경향 (백혈병, 재생불량성 빈혈, 자반병, 태반박리, 간경변, 암 등 및 수술 중ㆍ수술 후의 이상 출혈)" />
    <ARTICLE title="2. 국소적 섬유소용해항진과 관련있다고 생각되는 이상출혈 (폐출혈, 성기출혈, 신출혈, 전립선 수술 중ㆍ수술 후의 이상 출혈)" />
    <ARTICLE title="3. 습진, 피부염, 두드러기, 약진, 소아 구진두드러기" />
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="(정제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[아미노카프론산으로서 보통 성인 1일 3～12g을 3～4회에 분할 경구 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(주사제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[아미노카프론산으로서 보통 성인 1일 1～4g을 1～2회에 분할 점적 정맥주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[수술중 · 후의 출혈 등에는 필요에 따라 점적 정맥주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[소아는 연령에 따라 감량해서 피하 또는 근육주사할 수도 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 경고">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[앰플주사제는 용기 절단 시 유리파편이 혼입되어, 부작용을 초래할 수 있으므로 사용 시 유리파편혼입이 최소화될 수 있도록 신중하게 절단사용하되, 특히 어린이, 노약자 사용 시에는 각별히 주의할 것.(단, 앰플주사제에 한함)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 경우는 투여하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 2세 이하 영아]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 트롬빈을 투여중인 환자]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 다음 경우는 신중히 투여할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 혈전이 있는 환자(뇌혈전, 심근경색, 혈전 정맥염등) 및 혈전증을 일으킬 우려가 있는 환자(혈전을 안정화할 우려가 있다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 소모성 응고 장애가 있는 환자(헤파린등과 병용한다.)(혈전을 안정화할 우려가 있다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 이 약 및 이 약 구성 성분에 과민 반응 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 임부 또는 임신의 가능성이 있는 여성]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 이상반응">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 과민반응 : 드물게 가려움, 발진, 홍조등의 과민증상이 나타나는 경우에는 투여를 중지하는 등 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 소화기계 : 드물게 식욕부진, 구역, 설사,때때로 속쓰림 등의 증상이 나타날 수 있다(단, 정제에 한함).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 기타 : 드물게 권태감, 결막홍조, 비폐색,어지러움이 나타나는 경우에는 투여를 중지하는 등 적절한 처치를 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 트롬빈 : 혈전형성을 촉진하는 작용이 있어, 병용시 혈전형성이 증가하므로 병용투여하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 지혈성 장기제제, 헤모코아귤라제와의 대량병용투여에 의해서 혈전형성 작용이 나타날 수 있으므로 병용하는 경우에는 신중하게 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) Batroxobin과의 병용투여에 의해 혈전색전증을 일으킬 수 있으므로 병용하는 경우에는 신중하게 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) Eptacog alfa 등의 응고인자제제와 병용시 구강 등 선용계활성이 강한 부위에서 응고작용이 강화될 수 있으므로 신중하게 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 임부 및 수유부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 임신에 대한 안전성은 확립되지 않았으므로, 임부 또는 임신의 가능성이 있는 여성에게는 치료상의 유익성이 위험성을 상회한다고 판단되는 경우에만 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약이 모유중으로 분비되는지는 알려지지 않았으나, 수유부에게 투여할 때에는 주의하여야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 고령자에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[일반적으로 고령자는 생리기능이 저하되어 있으므로 감량하는 등 주의한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 적용상의 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[점적 정주 시 급속히 투여하면 드물게 구역, 흉내 불쾌감, 심계항진, 혈합저하, 서맥, 부정맥등이 나타날 수 있으므로 생리식염액, 5% 포도당액 또는 링겔액으로 희석하여 서서히 정맥내에 투여한다.(단, 주사제에 한함)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="9. 기타">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 항플라스민제(트라넥사민산)와 지혈성 장기제제(리피드트롬보 플라스틴)와의 정맥내 대량병용에 의한 동물실험에서 혈전형성 이 나타난다는 보고가 있다.(단, 주사제에 한함)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 대량투여에 의해 근육병증이 나타났다는 보고가 있으므로, 투여가 장기화되는 경우에는 CK(CPK)치를 측정하는 것이 바람직하다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('시노트림정(수출명:코트리메디코어들츠정,박트리메디코어들츠정)', '197600141', '일동제약(주)', '1정 중 500.0밀리그램|트리메토프림|USP|80.0|밀리그램|;1정 중 500.0밀리그램|설파메톡사졸|KP|400.0|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[&nbsp;]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[○ 유효균종]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[대장균, 시트로박터, 클레브시엘라, 엔테로박터, 프로테우스(프로테우스 불가리스, 프로테우스 미라빌리스), 프로비덴시아 레트게리, 모르가넬라 모르가니, 장구균, 헤모필루스 인플루엔자, 시겔라, 티푸스균, 파라티푸스균, 임균, 폐렴연쇄구균, 뉴우모시스티스 카리니, 화농연쇄구균, 포도구균]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[○ 적응증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[- 급만성기관지염, 기관지확장증, 폐렴]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[- 부비동염, 중이염]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[- 급만성방광염, 신우신염, 요도염, 전립선염, 임질]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[- 장티푸스, 파라티푸스, 장염, 세균성이질]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[&nbsp;]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="1. 성인 및 12세 이상의 소아">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1회 2정을 1일 2회 12시간마다 식후 경구투여한다. 14일 이상 장기투여 시 유지량으로 1회 1정을 1일 2회 투여하고 심한 감염증에는 최대용량으로 1회 3정을 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 12세 미만의 소아">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[다음 용량을 참고하여 체중 kg당 트리메토프림 6 mg과 설파메톡사졸 30 mg을 1일 2회 분할 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6～11세 : 1회 1정, 1일 2회]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3～5세 : 1회 1/2정, 1일 2회]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 신장애 환자">
      <PARAGRAPH tagName="table" textIndent="" marginLeft=""><![CDATA[<tbody> 
 <tr> 
  <td> <p>크레아티닌 청소율(mL/분)</p> </td> 
  <td> <p>용량</p> </td> 
 </tr> 
 <tr> 
  <td> <p>30 이상</p> <p>15～30</p> <p>15 이하</p> </td> 
  <td> <p>정상용량</p> <p>정상용량의 1/2</p> <p>투여가 권장되지 않는다</p> </td> 
 </tr> 
</tbody>]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 뉴우모시스티스 카리니 폐렴">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[체중 kg당 트리메토프림 20 mg과 설파메톡사졸 100 mg을 14일간 1일 4회 분할 투여한다. 급성감염증에는 최소한 5일간 또는 증상이 소실된 후에도 2일간 더 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[* 위장장애를 최소화 할 수 있도록 음식이나 음료와 함께 투여하는 것이 바람직하다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[* 2개월 미만의 영아에는 투여하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[* 연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 경고">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 인후통, 발열, 관절통, 기침, 호흡곤란, 창백, 자색반, 황달 등은 피부점막안증후군(스티븐스-존슨증후군), 중독성표피괴사용해(리엘증후군), 전격성간괴사, 무과립구증, 재생불량빈혈, 기타 혈액질환 및 호흡기계 과민반응을 포함한 중증 이상반응의 초기증상일 수도 있으므로 피부발진이나 다른 이상반응의 증상이 나타나는 즉시 투여를 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 연쇄구균에 의한 인두염 치료에는 사용하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 앰플주사제는 용기 절단시 유리파편이 혼입되어, 부작용을 초래할 수 있으므로 사용시 유리파편 혼입이 최소화 될 수 있도록 신중하게 절단 사용하되, 특히 어린이, 노약자 사용시에는 각별히 주의 할 것(단, 앰플주사제에 한함.).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 방부제로 쓰이는 피로아황산나트륨의 설파이트로 인해 특정 감수성 환자에게서 과민반응, 중증 천식 등의 알레르기 반응을 일으킬 수 있다(단, 젠시아설파메톡사졸과트리메토프림ㆍ주, 세바트림주에 한함.).]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 투여하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약 또는 이 약의 구성성분에 과민반응의 병력이 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 임부 또는 임신하고 있을 가능성이 있는 여성 및 수유부]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 신생아, 미숙아, 저체중출생아 및 2개월 미만의 영아]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 글루코오스-6-인산 탈수소효소(G-6-PD) 결핍 환자(용혈을 일으킬 우려가 있다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 간세포조직이 현저히 손상된 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 혈장 농도를 모니터링 할 수 없을 정도로 중증인 신부전 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 중증 혈액질환 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 포르피린증 환자]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 다음 환자에는 신중히 투여할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 혈액장애 또는 그 병력이 있는 환자인 경우에는 투여하지 않는 것을 원칙으로 하나 부득이하게 투여할 경우에는 신중히 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 본인 또는 부모, 형제가 기관지천식, 발진, 두드러기 등의 알레르기 증상을 일으키기 쉬운 체질의 환자 또는 다른 약물에 대한 과민반응의 병력이 있는 환자인 경우에는 투여하지 않는 것을 원칙으로 하나 부득이하게 투여할 경우에는 신중히 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 간장애 환자(간장애를 악화시킬 수 있다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 신장애 환자(혈중농도가 지속될 수 있으므로, 감량 등을 고려한다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 고령자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 갑상선기능부전 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 엽산결핍 또는 대사이상 환자(이미 위적출술을 받은 환자, 다른 엽산대사길항제를 투여받고 있는 환자, 선천성 엽산대사이상 환자 등)(엽산결핍을 악화시켜 거대적아구성빈혈을 일으킬 수 있다.)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 이상반응">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 혈액계 : 때때로 과립구감소증, 용혈성빈혈, 범혈구감소증, 혈소판감소증, 드물게 백혈구감소증, 재생불량빈혈, 거대적아구성빈혈(초기증상 : 발열, 인후통, 구내궤양, 피부점막 및 결막의 창백, 자색반 등)이 나타날 수 있으며, 매우 드물게 무과립구증, 메트헤모글로빈혈증, 호중구감소증, 저프로트롬빈혈증, 호산구증가증 등이 나타날 수 있으므로 충분히 관찰하고 이러한 증상이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 쇽 : 드물게 아나필락시, 쇽 증상(초기증상 : 불쾌감, 구내이상감, 쌕쌕거림, 어지러움, 변의, 이명, 발한 등)이 나타날 수 있으므로 충분히 관찰하고 이러한 증상이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 피부 : 결막 및 공막충혈, 때때로 발진, 가려움, 칸디다증이 나타날 수 있으며 드물게 피부점막안증후군(스티븐스-존슨증후군), 중독성표피괴사용해(리엘증후군), 홍반, 물집, 두드러기, 광과민반응, 매우 드물게 탈락피부염, 전신홍반루프스(SLE), 자색반 등이 나타날 수 있으므로 충분히 관찰하고 이러한 증상이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 간장 : 때때로 중증 간장애, AST/ALT, ALP, γ-GTP의 상승, 드물게 황달, 혈청빌리루빈의 상승, 간염, 간괴사 등이 나타날 수 있으므로 충분히 관찰하고 이러한 증상이 나타나는 경우에는 감량ㆍ휴약 등의 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 신장 : 감뇨와 무뇨를 동반한 중독성신증이 나타날 수 있으며 때때로 급성신부전, 드물게 신장애(BUN 및 혈청크레아티닌 상승, 혈뇨 등), 간질성신염, 결정뇨 등이 나타날 수 있으므로 충분히 관찰하고 이러한 증상이 나타나는 경우에는 감량ㆍ휴약 등의 적절한 처치를 한다. 또한 심장성 부종 환자에서 이뇨 증가를 유도할 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 신경계 : 건망증, 쇠약, 피로, 불면증, 때때로 두통이 나타날 수 있으며 드물게 말초신경병증, 졸음, 어지러움, 운동실조, 경련, 무균수막염 또는 수막염 유사증상, 마비, 무력증, 권태감, 이명, 우울, 환각, 일시적인 근시 등의 증상이 나타날 수 있으므로 충분히 관찰하고 이러한 증상이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 호흡기계 : 드물게 호산구 증가, 발열, 기침, 가래, 호흡곤란, 흉부X선 이상 등이 나타나는 호산구성폐침윤, 간질성폐렴 등이 나타날 수 있으므로 충분히 관찰하고 이러한 증상이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 소화기계 : 때때로 위장장애, 식욕부진, 구역, 구토, 설사, 복통, 위부불쾌감, 혀염, 구각염, 구내염, 담즙울체, 드물게 혈변, 변비, 구갈, 잇몸염, 미각이상 등이 나타날 수 있으며 혈변이 나타나는 경우에는 투여를 중지한다. 또한 급성췌장염 또는 위막성대장염 등의 혈변을 수반하는 중증의 대장염 등이 나타날 수 있으므로 충분히 관찰하고 이상이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[9) 내분비계 : 설폰아미드계 약물은 갑상선종 유발물질, 이뇨제(아세타졸라미드 및 치아짓계) 및 경구 혈당강하제 등과 화학적으로 유사성을 가지고 있어서 이뇨작용과 저혈당증이 드물게 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[10) 근골격계 : 드물게 관절통, 근육통이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[11) 기타]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(1) 정맥내 투여 시 드물게 국소반응, 통증 및 경미한 자극, 혈전정맥염 등이 나타날 수 있다(단, 주사제에 한함.).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(2) 신장애 환자에 투여하는 경우 때때로 저혈당 발작이 나타날 수 있으므로 충분히 관찰하고 이러한 증상이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(3) 포도막염, 추위 또는 때때로 발열, 열감, 드물게 혈압상승, 혈압강하, 심계항진, 흉부불쾌감, 발한, 안면홍조, 부종, 혈색소뇨증, 저칼륨혈증, 저혈당증, 대사산증, 가성패혈증 등이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(4) 다른 약물과 마찬가지로 이 약의 성분에 과민한 환자에서 알레르기 반응이 일어날 수 있다(예 : 발열, 맥관부종, 아나필락시 반응, 혈청병). 결절동맥주위염과 알레르기성 심근염이 보고되었다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(5) 고칼륨혈증, 저나트륨혈증 등의 전해질이상이 나타날 수 있으므로 충분히 관찰하고 이상이 확인되는 경우에는 투여를 중지하고 전해질 보정 등의 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(6) 근육통, 무력감, CK(CPK)상승, 혈중 및 요중 미오글로빈 상승 등을 특징으로 하는 횡문근융해증이 나타날 수 있으며, 이것에 수반하여 급격한 신기능의 악화, 급성신부전 등의 중증 증상에 이를 수 있으므로 충분히 관찰하고 이상이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(7) 장기 또는 반복투여 시 박테리아 또는 진균에 의한 중복감염(예 : 아구창, 음문질염 등)이 나타날 수 있다. 또한 1개월 이상 장기투여 시 정자형성억제작용이 나타날 수 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 일반적 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 경구제 투여가 불가능하거나 높은 혈중농도로 빨리 도달할 필요가 있는 경우에만 투여한다(단, 주사제에 한함.).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약의 사용에 있어서 내성균의 발현 등을 방지하기 위하여 가능하면 감수성을 확인하고 치료상 필요한 최소 기간만 투여하는 것이 바람직하다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 사용상의 주의사항, 효능ㆍ효과 및 용법ㆍ용량에 특히 유의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 혈액장애, 쇽 등을 예측하기 위해 충분한 문진을 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 뉴우모시스티스 카리니 폐렴에 대하여 이 약을 고용량(용법ㆍ용량 외) 투여한 예에서, 진행성 또는 가역성 혈청 칼륨 농도 증가가 일어날 수 있으며 중증 이상반응(고칼륨혈증, 저나트륨혈증 등)이 발현되었다는 보고가 있다. 권장용량의 트리메토프림을 투여하더라도 칼륨대사장애 또는 신부전 환자에서는 고칼륨혈증이 유발될 수 있다. 따라서 이러한 환자들에게 고칼륨혈증을 유발하는 것으로 알려진 약물을 병용투여할 경우에는 혈청 칼륨 농도를 세심하게 모니터링해야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 이 약을 투여받은 비당뇨성 환자에서 치료 수일 후 드물게 저혈당증이 관찰되었다. 특히 신기능 이상 환자, 간질환 환자, 영양실조 환자 또는 이 약을 고용량 투여받고 있는 환자들에 있어 저혈당이 유발될 가능성이 크므로 주의해야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 혈액장애, 쇽 등의 중증 이상반응이 나타날 수 있으므로 특히 이러한 이상반응 발현에 유의하고 다른 약이 효과가 없거나 투여가 불가능한 경우에만 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 투여개시에 앞서 중요한 이상반응에 대하여 환자에게 설명하고 혈액장애(빈혈, 출혈경향 등), 발진 등의 피부이상이 나타날 경우에는 신속히 의사와 상의하도록 지도한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[9) 이 약을 장기간 투여중인 환자는 정기적인 혈액검사를 실시하고, 어느 혈액인자라도 유의성 있는 감소를 나타내면 투여를 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[10) 이 약을 장기간 투여중인 환자(특히 신장애 환자)는 정기적으로 요검사 및 신기능 검사를 실시한다. 치료 중에 적절한 수액투여 및 요배출을 하여 결정뇨, 결석을 방지하여야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[11) 트리메토프림은 페닐알라닌 대사를 저해한다고 알려져 있으나 적절한 식이제한을 하는 페닐케톤뇨증 환자에게는 중요하지 않다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[12) AIDS 환자의 뉴우모시스티스 카리니 폐렴 치료시, 이상반응(고칼륨혈증, 저나트륨혈증 특히, 발진, 발열, 백혈구감소증, 트랜스아미나제 상승) 발현이 크게 증가한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[13) 아세틸화가 느린 환자는 설폰아미드계 약물에 대한 특이체질반응이 일어날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[14) 고령자, 엽산결핍증 환자 또는 신장애 환자에는 엽산결핍을 나타내는 혈액검사의 변화가 일어날 수 있다. 이는 엽산투여(folinic acid로서 1일 5～10 mg)로 회복될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[15) 이 약의 투여중에는 이상반응의 조기발견을 위해, 필요에 따른 임상검사(혈액검사, 간기능 검사, 신기능 검사, 혈중 전해질 등)를 실시한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[16) 거의 모든 항생제는 경증에서 중증에 이르는 범위의 위막성대장염을 일으킨다는 보고가 있다. 그러므로 이 약을 포함하여 항생제 투여 환자 중 지속적인 설사가 나타나는 환자에 대한 진단이 중요하다. 항생제 사용에 의해 장내세균총이 변화를 일으켜 클로스트리듐 균의 과잉번식을 일으킬 수 있다. 연구에 의하면 클로스트리듐 다이피셀(Clostridium difficile)에 의해 생산되는 독소가 항생제 관련 대장염의 주 원인으로 알려졌다. 위막성대장염으로 진단이 내려지면 치료를 시작해야한다. 경미한 위막성대장염은 투여중지에 의해 회복될 수 있다. 중등도 또는 중증의 경우에는 수액, 전해질, 단백질 보충 등의 처치를 하고 클로스트리듐 다이피셀에 대해 임상적으로 효과 있는 항생제로 치료한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[17) 14일 이상 투여하는 것은 치료상의 유익성이 위험성을 넘는다고 판단되는 경우에만 투여한다. 이때 정기적인 임상관찰, 임상검사에 의한 감시를 계속할 필요가 있다(단, 주사제에 한함).]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약은 항응고제(와파린)의 간대사를 억제함과 동시에 혈장단백에 결합한 항응고제와 치환하여 유리시키므로 병용투여 시 항응고제의 작용을 증강시키고 프로트롬빈 시간을 연장시킬 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약은 페니토인의 간대사를 억제하여 병용투여 시 페니토인의 작용을 증강시킬 수 있으므로 주의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 설폰아미드계 약물은 혈장단백에 결합한 메토트렉세이트와 치환하여 유리시키므로 병용투여 시 유리 메토트렉세이트 농도를 증가시켜 작용을 증강시키고 범혈구 감소 등이 나타날 수 있으므로 병용투여하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 이 약은 설폰아미드계 및 설포닐우레아계 경구 혈당강하제의 간대사를 억제함과 동시에 혈장단백에 결합한 경구 혈당강하제와 치환하여 유리시키므로 병용투여 시 이러한 약물의 작용을 증강시켜 저혈당이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 사이클로스포린(특히 신장이식후)과 병용투여 시 신기능 손상이 증가될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 지도부딘과 병용투여 시 지도부딘의 독성(과립구감소 등)이 증가된다는 보고가 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 디곡신 제제, 라미부딘과 병용투여 시 특히, 고령자에서 트리메토프림이 이러한 약물의 세뇨관분비를 저하시켜 혈중 농도가 상승할 수 있으므로 모니터링해야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 삼환계 항우울제(염산클로미프라민, 염산이미프라민, 염산아미트리프틸린 등)와 병용투여 시 삼환계 항우울제의 효과가 감약될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[9) 말라리아 예방을 목적으로 피리메타민을 1주에 25 mg 이상 복용하는 환자에게 이 약과 병용투여 시 거대적아구성빈혈이 나타났다는 보고가 있다. 또한 설파독신과의 병용투여에 의해서도 거대적아구성빈혈이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[10) 인도메타신, 프로베네시드, 페닐부타존, 살리실산, 설핀피라존과 병용투여 시 이 약의 작용이 증강될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[11) 이 약은 신장을 통해 배설되므로 신장을 통해 배설되는 프로카인아미드, 아만타딘과 같은 약물과 병용투여 시 경쟁적으로 작용하여 혈중 농도가 증가할 수 있으며 중독성 헛소리가 나타났다는 보고가 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[12) 이뇨제(주로 치아짓계)와 병용투여하는 환자들에게 자색반을 동반한 혈소판감소증의 발생빈도가 증가한다는 보고가 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[13) 디아페닐설폰과의 병용투여에 의해 혈액장애(거대적아구성빈혈, 범혈구 감소 등)가 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[14) 후천성면역결핍증(AIDS) 환자의 뉴우모시스티스 카리니 폐렴 치료시 폴리네이트칼슘과의 병용투여는 치료의 실패율과 사망율을 증가시킬 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[15) 경구 피임약과 병용투여 시 피임의 효과가 감약될 수 있으므로 다른 피임법을 추가로 사용하는 것이 바람직하다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[16) 치오펜탈과 병용투여 시 치오펜탈의 작용이 증강될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[17) 메테나민, 만델레이트염과 병용투여 시 결정뇨가 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[18) 제산제와 병용투여 시 이 약의 흡수가 감소할 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[19) 파라알데히드, 파라아미노벤조산에스테르(벤조카인, 프로카인, 테트라카인 등)와 병용투여 시 이 약의 효과가 감약될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[20) 바르비탈계 약물, 프리미돈, 파라아미노살리실산과 병용투여 시 이 약의 독성이 증강될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[21) 메르캅토푸린과 병용투여 시 메르캅토푸린의 흡수가 감소될 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[22) 리팜피신과 병용투여 시 리팜피신의 청소율이 감소할 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[23) 이 약의 엽산길항작용으로 엽산결핍증상이 나타날 수 있으므로 페니토인, 메토트렉세이트, 프리미돈, 파라아미노살리실산 등의 엽산결핍증상을 유발할 수 있는 약물과 병용투여 시 엽산결핍 발생률을 증가시킬 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[24) 다음 약물과의 병용투여에 의해 고칼륨혈증이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[칼륨제제, 칼륨보존성 이뇨제, 항전간제, 안지오텐신 Ⅱ 수용체 길항제, 비스테로이드소염제, 헤파린, 시클로스포린 및 타클로리무스, 트리메토프림]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 임부 및 수유부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 동물실험에서 기형발생작용이 보고되었고, 임신 중에 이 약을 단독 또는 병용투여받은 환자의 아이에게 선천성 이상이 나타났다는 보고가 있으므로 임부 또는 임신하고 있을 가능성이 있는 여성에는 투여하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약은 모유 중으로 이행하여 고빌리루빈혈증, 핵황달, 과민반응을 일으킬 수 있으므로 수유부에는 투여하지 않는다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 소아에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 고빌리루빈혈증을 일으킬 수 있으므로 신생아, 미숙아 및 저체중출생아에는 투여하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 2개월 미만의 영아에는 투여하지 않는다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="9. 고령자에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 고령자에 투여 시 신장애, 간장애 또는 다른 약물과의 병용투여와 관련하여 중증 이상반응이 발생할 수 있다. 중증 피부반응, 전신성 골수저해, 특이한 혈소판 감소 등이 고령자에게서 가장 자주 보고되는 중증 이상반응이다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약은 주로 신장으로 배설되기 때문에 신기능 저하가 많은 고령자에서는 높은 혈중농도가 유지될 위험이 있으므로 적절히 용량을 조절하는 등 신중히 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="10. 임상검사치에 대한 영향">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 메토트렉세이트와 병용투여 시 디히드로엽산환원효소(Dihydrofolate Reductase)를 이용한 메토트렉세이트 농도측정에서 높은 수치를 나타낼 수 있으므로 주의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 두 성분 모두 Jaffe alkaline picrate 반응검사를 방해하여 정상범위의 측정치를 약 10% 상승시킬 수 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="11. 과량투여시의 처치">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 증상 : 급성 과량투여 증상으로 구역, 구토, 설사, 두통, 이명, 어지러움, 혼미, 식욕부진, 졸음, 무의식, 발열, 황달, 우울과 혼돈 등의 정신장애 및 시각장애 등이 나타날 수 있으며 심각한 경우에는 결정뇨, 혈뇨, 무뇨가 나타날 수 있다. 만성 과량투여 시에는 혈소판감소증, 백혈구감소증 또는 거대적아구성빈혈로 발현되는 골수억제와 엽산결핍으로 인한 다른 혈액질환이 일어날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 치료 : 증상에 따라 다음과 같은 치료가 고려될 수 있다. 위세척, 구토, 이뇨제를 이용한 요로의 배출(요의 알카리화는 설파메톡사졸의 배설을 증가시킨다.), 혈액투석(주의 : 복막투석은 효과가 없다.), 혈구수와 전해질량 모니터링 등을 실시하며 중요한 혈액질환이나 황달이 발생하면, 특수처치를 하여야 한다. 엽산칼슘을 5～7일간 3～6 mg을 투여하여 조혈계에 대한 트리메토프림의 작용을 저해한다(단, 위세척, 구토는 경구제에 한함).]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="12. 적용상의 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[투여액이 혈관외부로 침투하여 국소자극 및 염증을 일으킬 수 있으며 이러한 경우에는 투여를 중단하고 다른 부위에 재투여한다(단, 주사제에 한함).]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="13. 기타">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[동물시험(랫트)에서 갑상선종 및 갑상선기능이상을 일으킨다는 보고가 있다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('일바돈크림(설파디아진은)', '197600142', '일동제약(주)', '1그램 중|설파디아진은|USP|10.0|밀리그램|설파디아진으로서 7mg, 은으로서 3mg', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2-3도 화상, 각종 피부궤양(욕창, 다리궤양, 방사선궤양, 당뇨병성괴저, 피부상처 등)으로 인한 다음 병원균의 감염증 : 녹농균, 엔테로박터속, 클레브시엘라속, 포도구균속, 용혈성연쇄구균, 칸디다속]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1일 1회 멸균장갑 등을 사용하여 환부를 덮을 수 있도록 충분한 두께(약 2-3㎜)로 직접 바르거나 거즈 등에 같은 두께로 발라 붙이고 붕대를 감는다. 또한 2일째 이후에는 전날 바른 크림을 깨끗한 거즈 등으로 닦아내거나 온수로 씻어낸 후 바른다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상의주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 다음 환자에는 투여하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약 또는 이 약 성분에 과민증 및 그 병력이 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 생후 2개월이내 신생아, 조산아, 초산아, 미숙아]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 임신말기, 수유부]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 경증 화상]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 신중히 투여할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 약물 또는 광과민증의 병력이 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 홍반성루푸스환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 글루코오스-6-인산탈수소효소(G-6-PD)결손증 환자(용혈현상이 나타날 수 있다)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 간ㆍ신장애 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 이 약은 프로필렌글리콜을 함유하고 있으므로 이 성분에 과민하거나 알레르기 병력이 있는 환자에는 신중히 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 부작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 혈액계 : 백혈구감소증이 나타날 수 있으므로 관찰을 충분히 하고, 이상이 인정되는 경우에는 사용을 중지하고 적절한 처치를 한다. 또한 빈혈, 혈소판 감소가 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 피부]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[① 피부괴사가 발생했다는 보고가 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[② 때때로 동통이 발생할 수 있으므로 이러한 경우에는 필요에 따라 진통제를 투여하고 동통이 개선되지 않는 경우에는 사용을 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[③ 과민증 : 발진, 발적, 광과민증, 접촉피부염 등이 나타날 수 있으므로 이러한 경우 사용을 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 신장 : 간질성신염이 나타났다는 보고가 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 균교대증 : 이 약 내성균, 비감수성균에 의한 화농성감염증이 나타날 수 있으므로 이러한 경우 사용을 중지한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 일반적주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 경증화상에 사용하면 동통이 나타날 수 있으므로 사용하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 설파제의 전신투여와 같은 증상의 부작용이 나타날 수 있으므로 장기간 사용을 피한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 감작될 수 있으므로 관찰을 충분히 하고 감작징후(가려움, 발적, 종창, 구진, 소수포 등)가 나타날 경우에는 사용을 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 광범위 화상에 사용할 경우 이 약 중의 프로필렌글리콜에 의해 고삼투압상태가 나타날 수 있으므로 정기적으로 혈청삼투압을 측정하고, 이상이 인정될 경우에는 투여를 중지하는 등 적절한 처치를 한다(특히 유아, 소아의 경우 주의한다).]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 외피용 효소제제의 작용을 약화시킬 수 있으므로 이들 약물과 병용시 주의한다[은(銀)이 효소의 SH기와 결합하여 효소활성을 약화시킬 가능성이 있다].]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약을 광범위한 화상부위를 가지고 있는 환자에게 투여시 혈청 설파디아진 치가 치료수준에 도달했을 때 경구용 혈당강하제와 페니토인을 투여하는 것은 위험할 수 있으므로 혈중 설파디아진 치를 모니터링한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 넓은 면적의 화상부위를 가지고 있는 환자에게 시메티딘을 병용투여했을 때 백혈구감소 발생빈도를 증가시킬 수 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 임부 및 수유부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[임신 중 이 약 투여에 관한 안전성이 확립되어 있지 않으므로 임부 또는 임신하고 있을 가능성이 있는 부인에는 화상부위가 20%이상이거나 치료상의 유익성이 위험성을 상회한다고 판단되는 경우에만 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 소아에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[소아에 대한 안전성 및 유효성이 확립되어 있지 않다. 특히 미숙아, 신생아(생후 2개월이내)는 핵황달을 일으킬 가능성이 증가된다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 과량투여시의 처치">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[광범위 화상환자나 약물과량 흡수징후가 보이는 환자에 있어서는 탈수나 신장손상 가능성이 있으므로 이때 최적의 체액평형을 유지해야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="9. 적용상의 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 안과용으로 사용하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약을 바를 경우에는 가능한 한 온수욕, 샤워 등을 함께 하여 환부를 깨끗이 하고 괴사조직을 제거한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 다른 약과 혼합해서 사용하지 않는다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="10. 저장상의 주의사항">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[어린이의 손이 닿지 않는 곳에 보관한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="11. 기타">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[동물실험에서 여러 장기(간, 췌장, 장간막림프절 등)에의 은침착과 가역적으로 가벼운 정도의 ALP의 상승이 보고되었다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('유한세파졸린주사500mg(수출용:오리졸린주500mg)', '197600164', '(주)유한양행', '1바이알(500mg) 중|세파졸린나트륨|KP|500|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="○ 유효균종">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[폐렴연쇄구균, 클레브시엘라, 인플루엔자균, 포도구균, 대장균, 프로테우스 미라빌리스, 엔테로박터, 연쇄구균, 장내구균]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="○ 적응증">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 기관지염, 폐렴, 인후두염 등 호흡기 감염증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 요로감염증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 피부 및 연조직감염증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 담관염, 담낭염]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 골 및 관절감염증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 전립선염, 부고환염 등 생식기감염증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 패혈증, 심내막염]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="○ 성인 : 세파졸린으로서 1일 1g(역가)을 2회 분할 정맥 또는 근육주사한다." />
    <ARTICLE title="○ 소아 : 1일 체중 Kg당 20～40mg(역가)을 2회 분할 정맥 또는 근육주사한다." />
    <ARTICLE title="○ 증상 및 감염균의 감수성에서 효과가 불충분하다고 판단되는 경우에는 1일 성인은 1.5～3g(역가), 소아는 체중 Kg당 50mg(역가)을 3회 분할 주사한다. 증상이 특히 중증의 경우에는 1일 성인은 5g(역가), 소아는 체중 Kg당 100mg(역가)까지 투여할 수 있다.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="&amp;lt;주사액의 조제&amp;gt;">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[정맥주사 시는 주사용수, 생리식염주사액 또는 5% 포도당 주사액에 녹여 주사한다. 점적 정맥주사 시 녹일 때 주사용수는 사용하지 않는다(등장액이 아님.).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[근육주사 시 0.5% 리도카인주사액 2～3mL에 녹여 주사한다. 또한 온도에 의한 용해도 차에 의하여 가끔 백탁하는 경우가 있으나 이 경우는 온탕으로 따뜻하게 하여 투명한 용액으로 만든 후 사용한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[용해 후 신속히 사용하고 보존할 필요가 있는 경우에는 차광하여 실온에서 48시간이내에 사용한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 다음 환자에는 투여하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약에 의하여 쇽의 병력이 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 세팔로스포린계 항생물질에 과민반응 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 리도카인 등의 아닐리드계 국소마취제에 과민반응의 병력이 있는 환자(근육주사에 한함.)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 신중히 투여할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약 또는 세펨계 항생물질에 과민반응의 병력이 있는 환자에는 투여하지 않는 것을 원칙으로 하나 부득이 투여할 경우에는 신중히 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 페니실린계 항생물질에 과민반응의 병력이 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 본인 또는 부모, 형제가 기관지천식, 발진, 두드러기 등의 알레르기 증상을 일으키기 쉬운 체질인 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 중증의 신장애 환자(혈중농도가 지속되므로 투여간격을 두고 사용한다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 경구섭취가 불량한 환자 또는 비경구영양 환자, 고령자, 전신상태가 나쁜 환자 (비타민 K 결핍증상이 나타날 수 있으므로 충분히 관찰한다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 위장관질환의 병력이 있는 환자(특히, 대장염)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 이상반응">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 쇽 : 드물게 쇽을 일으킬 수 있으므로 충분히 관찰하고 불쾌감, 구내이상감, 천명, 어지러움, 변의, 이명, 발한 등이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 과민반응 : 아나필락시양 증상(호흡곤란, 전신 홍반, 맥관부종), 발진, 두드러기, 홍반, 가려움, 발열, 두통, 관절통, 수포, 작열감 등이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 혈액계 : 드물게 과립구 감소, 호산구 증가, 용혈성 빈혈, 혈소판 감소, 호중구 감소, 백혈구 감소, 고혈소판혈증이 나타날 수 있으므로 이상이 인정되는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 피부 : 스티븐스-존슨증후군, 독성표피괴사용해가 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 간장 : 드물게 AST, ALT, ALP의 상승 등의 간기능 이상이 나타날 수 있으므로 이상이 인정되는 경우에는 신중히 투여한다. 또한 이와 함께 황달 등이 나타나는 경우에는 투여를 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 신장 : BUN 상승 드물게 혈청크레아티닌 상승, 급성 신부전 등의 중증의 신장애가 나타날 수 있으므로 정기적으로 검사를 실시하는 등 충분히 관찰하고 이상이 인정되는 경우에는 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 소화기계 : 드물게 위막성대장염 등의 혈변을 수반하는 중증의 대장염이 나타날 수 있다. 복통, 빈번한 설사가 나타나는 경우에는 즉시 투여를 중지하는 등 적절한 처치를 한다. 또한 때때로 구역, 구토, 식욕부진, 아구창 등이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 호흡기계 : 드물게 발열, 기침, 호흡곤란, 흉부 X선 이상, 호산구 증가 등을 수반하는 간질성 폐렴, 호산구성 폐침윤 등이 나타날 수 있으므로 이러한 증상이 나타나는 경우에는 투여를 중지하고 코르티코이드 투여 등 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[9) 중추신경계 : 신부전 환자에 대량 투여 시 경련 등의 신경증상을 일으킬 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[10) 균교대증 : 드물게 구내염, 칸디다증이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[11) 비타민결핍증 : 드물게 비타민 K 결핍증상(저프로트롬빈혈증, 출혈경향 등), 비타민 B군 결핍증상(설염, 구내염, 식욕부진, 신경염 등)이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[12) 기타 : 드물게 두통, 전신권태감, 생식기, 항문 가려움(음문 가려움, 생식기 모닐리아증, 질염)이 나타날 수 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 일반적 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약의 사용에 있어서 내성균의 발현을 방지하기 위하여 감수성을 확인하고 치료 상 필요한 최소 기간만 투여하는 것이 바람직하다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 쇽 등의 반응을 예측하기 위해 충분히 문진하고 사전에 피부반응 시험을 실시하는 것이 바람직하다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 쇽 발생에 대비하여 구급처치준비를 해두고 투여 후에도 환자를 안정한 상태에서 충분히 관찰한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 만약 과민반응이 발생되면 보통 적절한 치료제(예 : 산소투여, 정맥 수액 공급, 에피네프린, 기타 승압 아민제, 항히스타민제 또는 코르티코이드), 기도확보로 처치해야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 프로베네시드와 병용투여 시 이 약의 세뇨관 배설 속도가 감소되어 혈중농도를 지속시킬 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 다른 세팔로스포린계, 아미노글리코사이드계 항생물질, 푸로세미드 등의 이뇨제(폴리믹신 B, 콜리스틴) 등 신독성 약물과 병용투여 시 신독성이 증가될 수 있으므로 신중히 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) INR(국제정상화비율)의 변화 : 이 약을 포함한 항생물질을 항응고제와 동시에 투여받은 환자에서 항응고 활성의 증가가 보고되었다. 감염성질환(그리고 염증성 과정을 동반한), 환자의 연령과 일반적인 상태는 위험요소가 된다. 비록 이 약과 와파린의 상호작용이 임상시험을 통해 밝혀지지는 않았지만 INR 모니터링을 실시하여야 하고 필요한 경우 경구용 항응고제의 용량을 적절히 조절한다. 항생물질의 일부 종류들, 특히 플루오로퀴놀론, 마크로라이드, 사이클린, 코트리목사졸과 일부 세팔로스포린의 경우는 더 심하다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 임부 및 수유부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 임신 중의 투여에 대한 안전성이 확립되어 있지 않으므로 임부 또는 임신하고 있을 가능성이 있는 여성에는 치료 상의 유익성이 위험성을 상회한다고 판단되는 경우에만 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 모유로의 이행이 보고되어 있으므로 수유부에는 투여하지 않는 것이 바람직하다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 소아에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[미숙아, 신생아에 대한 안전성은 확립되어 있지 않으므로 투여하지 않는 것이 바람직하다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 고령자에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[고령자의 경우 다음과 같은 점을 주의하여 용량 및 투여간격에 유의하는 등 환자의 상태를 관찰하여 신중히 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 생리기능이 저하되어 있으므로 이상반응 발현이 나타나기 쉽다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 비타민 K 결핍으로 인하여 출혈경향이 나타날 수 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="9. 임상검사치에 대한 영향">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 테스테이프반응을 제외한 베네딕트시약, 펠링시약, 클리니테스트에 의한 요당검사에서 위양성이 나타날 수 있으므로 주의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 직접 쿰스시험 양성이 나타날 수 있으므로 주의한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="10. 적용상의 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 정맥내 대량투여에 의해 드물게 혈관통, 혈전성 정맥염을 일으킬 수 있으므로 주사액의 조제, 주사부위, 주사방법 등에 대하여 충분히 주의하고 주사속도는 가능한 천천히 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 근육주사는 정맥주사가 곤란한 경우에만 사용한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="11. 기타">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[동물실험에서 토끼에 체중 Kg당 500mg이상 투여한 경우 신장애가 나타났다는 보고가 있다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('유한세파졸린주사1000mg(수출명:오리졸린주1000mg,메디세파졸린1000mg,유한세졸주1000mg,마이코신주, 레프졸린주)', '197600165', '(주)유한양행', '1바이알(1그램) 중|세파졸린나트륨|USP|1,000|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="○ 유효균종">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[폐렴연쇄구균, 클레브시엘라, 인플루엔자균, 포도구균, 대장균, 프로테우스 미라빌리스, 엔테로박터, 연쇄구균, 장내구균]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="○ 적응증">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 기관지염, 폐렴, 인후두염 등 호흡기 감염증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 요로감염증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 피부 및 연조직감염증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 담관염, 담낭염]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 골 및 관절감염증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 전립선염, 부고환염 등 생식기감염증]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 패혈증, 심내막염]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="○ 성인 : 세파졸린으로서 1일 1g(역가)을 2회 분할 정맥 또는 근육주사한다." />
    <ARTICLE title="○ 소아 : 1일 체중 Kg당 20～40mg(역가)을 2회 분할 정맥 또는 근육주사한다." />
    <ARTICLE title="○ 증상 및 감염균의 감수성에서 효과가 불충분하다고 판단되는 경우에는 1일 성인은 1.5～3g(역가), 소아는 체중 Kg당 50mg(역가)을 3회 분할 주사한다. 증상이 특히 중증의 경우에는 1일 성인은 5g(역가), 소아는 체중 Kg당 100mg(역가)까지 투여할 수 있다.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[연령, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="&amp;lt;주사액의 조제&amp;gt;">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[정맥주사 시는 주사용수, 생리식염주사액 또는 5% 포도당 주사액에 녹여 주사한다. 점적 정맥주사 시 녹일 때 주사용수는 사용하지 않는다(등장액이 아님.).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[근육주사 시 0.5% 리도카인주사액 2～3mL에 녹여 주사한다. 또한 온도에 의한 용해도 차에 의하여 가끔 백탁하는 경우가 있으나 이 경우는 온탕으로 따뜻하게 하여 투명한 용액으로 만든 후 사용한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[용해 후 신속히 사용하고 보존할 필요가 있는 경우에는 차광하여 실온에서 48시간이내에 사용한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상의주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 다음 환자에는 투여하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약에 의하여 쇽의 병력이 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 세팔로스포린계 항생물질에 과민반응 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 리도카인 등의 아닐리드계 국소마취제에 과민반응의 병력이 있는 환자(근육주사에 한함.)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 신중히 투여할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약 또는 세펨계 항생물질에 과민반응의 병력이 있는 환자에는 투여하지 않는 것을 원칙으로 하나 부득이 투여할 경우에는 신중히 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 페니실린계 항생물질에 과민반응의 병력이 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 본인 또는 부모, 형제가 기관지천식, 발진, 두드러기 등의 알레르기 증상을 일으키기 쉬운 체질인 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 중증의 신장애 환자(혈중농도가 지속되므로 투여간격을 두고 사용한다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 경구섭취가 불량한 환자 또는 비경구영양 환자, 고령자, 전신상태가 나쁜 환자 (비타민 K 결핍증상이 나타날 수 있으므로 충분히 관찰한다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 위장관질환의 병력이 있는 환자(특히, 대장염)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 이상반응">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 쇽 : 드물게 쇽을 일으킬 수 있으므로 충분히 관찰하고 불쾌감, 구내이상감, 천명, 어지러움, 변의, 이명, 발한 등이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 과민반응 : 아나필락시양 증상(호흡곤란, 전신 홍반, 맥관부종), 발진, 두드러기, 홍반, 가려움, 발열, 두통, 관절통, 수포, 작열감 등이 나타나는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 혈액계 : 드물게 과립구 감소, 호산구 증가, 용혈성 빈혈, 혈소판 감소, 호중구 감소, 백혈구 감소, 고혈소판혈증이 나타날 수 있으므로 이상이 인정되는 경우에는 투여를 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 피부 : 스티븐스-존슨증후군, 독성표피괴사용해가 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 간장 : 드물게 AST, ALT, ALP의 상승 등의 간기능 이상이 나타날 수 있으므로 이상이 인정되는 경우에는 신중히 투여한다. 또한 이와 함께 황달 등이 나타나는 경우에는 투여를 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 신장 : BUN 상승 드물게 혈청크레아티닌 상승, 급성 신부전 등의 중증의 신장애가 나타날 수 있으므로 정기적으로 검사를 실시하는 등 충분히 관찰하고 이상이 인정되는 경우에는 중지하고 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 소화기계 : 드물게 위막성대장염 등의 혈변을 수반하는 중증의 대장염이 나타날 수 있다. 복통, 빈번한 설사가 나타나는 경우에는 즉시 투여를 중지하는 등 적절한 처치를 한다. 또한 때때로 구역, 구토, 식욕부진, 아구창 등이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 호흡기계 : 드물게 발열, 기침, 호흡곤란, 흉부 X선 이상, 호산구 증가 등을 수반하는 간질성 폐렴, 호산구성 폐침윤 등이 나타날 수 있으므로 이러한 증상이 나타나는 경우에는 투여를 중지하고 코르티코이드 투여 등 적절한 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[9) 중추신경계 : 신부전 환자에 대량 투여 시 경련 등의 신경증상을 일으킬 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[10) 균교대증 : 드물게 구내염, 칸디다증이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[11) 비타민결핍증 : 드물게 비타민 K 결핍증상(저프로트롬빈혈증, 출혈경향 등), 비타민 B군 결핍증상(설염, 구내염, 식욕부진, 신경염 등)이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[12) 기타 : 드물게 두통, 전신권태감, 생식기, 항문 가려움(음문 가려움, 생식기 모닐리아증, 질염)이 나타날 수 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 일반적 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약의 사용에 있어서 내성균의 발현을 방지하기 위하여 감수성을 확인하고 치료 상 필요한 최소 기간만 투여하는 것이 바람직하다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 쇽 등의 반응을 예측하기 위해 충분히 문진하고 사전에 피부반응 시험을 실시하는 것이 바람직하다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 쇽 발생에 대비하여 구급처치준비를 해두고 투여 후에도 환자를 안정한 상태에서 충분히 관찰한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 만약 과민반응이 발생되면 보통 적절한 치료제(예 : 산소투여, 정맥 수액 공급, 에피네프린, 기타 승압 아민제, 항히스타민제 또는 코르티코이드), 기도확보로 처치해야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 프로베네시드와 병용투여 시 이 약의 세뇨관 배설 속도가 감소되어 혈중농도를 지속시킬 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 다른 세팔로스포린계, 아미노글리코사이드계 항생물질, 푸로세미드 등의 이뇨제(폴리믹신 B, 콜리스틴) 등 신독성 약물과 병용투여 시 신독성이 증가될 수 있으므로 신중히 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) INR(국제정상화비율)의 변화 : 이 약을 포함한 항생물질을 항응고제와 동시에 투여받은 환자에서 항응고 활성의 증가가 보고되었다. 감염성질환(그리고 염증성 과정을 동반한), 환자의 연령과 일반적인 상태는 위험요소가 된다. 비록 이 약과 와파린의 상호작용이 임상시험을 통해 밝혀지지는 않았지만 INR 모니터링을 실시하여야 하고 필요한 경우 경구용 항응고제의 용량을 적절히 조절한다. 항생물질의 일부 종류들, 특히 플루오로퀴놀론, 마크로라이드, 사이클린, 코트리목사졸과 일부 세팔로스포린의 경우는 더 심하다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 임부 및 수유부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 임신 중의 투여에 대한 안전성이 확립되어 있지 않으므로 임부 또는 임신하고 있을 가능성이 있는 여성에는 치료 상의 유익성이 위험성을 상회한다고 판단되는 경우에만 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 모유로의 이행이 보고되어 있으므로 수유부에는 투여하지 않는 것이 바람직하다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 소아에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[미숙아, 신생아에 대한 안전성은 확립되어 있지 않으므로 투여하지 않는 것이 바람직하다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 고령자에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[고령자의 경우 다음과 같은 점을 주의하여 용량 및 투여간격에 유의하는 등 환자의 상태를 관찰하여 신중히 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 생리기능이 저하되어 있으므로 이상반응 발현이 나타나기 쉽다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 비타민 K 결핍으로 인하여 출혈경향이 나타날 수 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="9. 임상검사치에 대한 영향">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 테스테이프반응을 제외한 베네딕트시약, 펠링시약, 클리니테스트에 의한 요당검사에서 위양성이 나타날 수 있으므로 주의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 직접 쿰스시험 양성이 나타날 수 있으므로 주의한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="10. 적용상의 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 정맥내 대량투여에 의해 드물게 혈관통, 혈전성 정맥염을 일으킬 수 있으므로 주사액의 조제, 주사부위, 주사방법 등에 대하여 충분히 주의하고 주사속도는 가능한 천천히 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 근육주사는 정맥주사가 곤란한 경우에만 사용한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="11. 기타">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[동물실험에서 토끼에 체중 Kg당 500mg이상 투여한 경우 신장애가 나타났다는 보고가 있다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('포타딘액(포비돈요오드)', '197600172', '삼일제약(주)', '100밀리리터 중|포비돈 요오드|USP|10.0|그램|유효요오드로서 1000mg', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[- 찢긴 상처, 화상, 창상의 살균소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[- 궤양, 농양의 살균소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[- 감염피부면의 소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[- 수술부위의 살균소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[- 주사 및 카테터부위의 소독]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1일 수회 환부에 적당량 바른다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상의주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 다음 환자에는 투여하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약 및 이 약 성분에 과민증 환자.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 갑상선기능 이상 환자(특히 결절성 갑상선종, 지방병성 갑상선종, 하시모토갑상선염 등)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 신부전 환자.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 신생아 및 6개월 미만의 영아.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 포진상 피부염 환자.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 방사성요오드치료전후]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 신중히 투여할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 중증의 환자(화상 환자 등)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 갑성선 질환의 병력이 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 신부전의 병력이 있는 환자]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 부작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 과민증 : 드물게 아나필락시양 증상(호흡곤란, 조홍, 두드러기 등), 요오드진 (iododerma) 등이 나타날 수 있으므로 이러한 경우에는 사용을 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 피부 : 때때로 동통, 가려움, 자극감, 발진, 발적, 피부염, 드물게 작열감, 피부궤양, 접촉피부염, 피부변색, 장기간 사용시 상처치유저해가 나타날 수 있다. 이러한 증상이 심할 경우에는 사용을 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 전해질, 대사 : 화상치료의 경우처럼 많은 양을 투여시 전해질 및 혈청의 삼투압장애, 대사성 산증이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 신장 : 화상치료의 경우처럼 많은 양을 투여시 신부전을 일으킬 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 혈액 : 장기간 사용시 시상하부-뇌하수체-갑상선-축기능의 변화와 가역반응에 의한 요오드의 분비량 증가가 나타날 수 있다. 또한 혈청 갑상선자극호르몬의 농도가 약 간 상승하고 T₄및 T₃감소가 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 대량투여 : 대량 또는 장기간의 접촉으로 일부 환자에서 일시적인 혈청 요오드결합 단백의 증가 보고가 있다. 광범위한 상해 또는 화상에 이 약의 반복적 투여로 요오드 의 전신흡수에 따른 여러 부작용(금속 맛을 느낌, 타액분비 증가, 목과 입에서의 작열 감 및 동통, 눈의 자극 및 부종, 피부반응, 배탈, 설사 및 폐부종으로 인한 호흡곤란)이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 기타 : 소독용으로 이 약을 과량사용시 요오드독성이 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 일반적 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 분만직전에 사용한 경우에는 분만후 되도록 빨리 신생아의 눈을 씻어주고 목욕시켜 부착되지 않도록 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약에 의한 치료는 보존적 치료임을 유의하고 증상의 개선경향이 보이지 않을 경우 는 외과적 요법 등을 고려한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 광범위한 화상을 입은 환자에게 이 약을 사용할 경우 수분공급이 충분히 이루어져야 한다. 산, 염기 대사 및 신기능을 관찰해야 하며 대사성 산증이나 신기능 이상이 있 을 경우 이 약의 사용을 중지한다(세정액, 세액/7.5% 제외).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 이 약을 화상으로 인한 궤양에 사용할 경우, 화상후의 2차 손상에 의해 발생하는 궤 양에만 사용하며 새로 발생한 화상에 대해서는 다른 적절한 요법을 고려한다(세정액, 세액/7.5% 제외).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 이 약은 섬광촬영술을 이용한 갑상선진단에 영향을 줄 수 있으므로 이 약의 투여와 섬광도 조사는 1～2주의 간격을 두고 실시한다. 또한 이 약의 투여중에는 갑상선기능 이 가역적으로 변화될 수 있으므로 주의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 이 약은 요오드함유제제이므로 대량투여 및 장기연용시에는 갑상선기능 이상에 주의 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 감염(복막염, 골수염 등) 발생 시에는 이 약의 사용전 생리식염수로 세정한다. 이 약의 접촉으로 박테리아로부터 내독소가 유리될 위험이 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 드물게 쇽(아나필락시)이 일어날 수 있으므로 사용후 갑자기 가려움, 부종, 가슴 쓰림 등과 동시에 안색창백, 수족냉감, 식은 땀, 숨가뿜 등의 중증의 증상이 나타나 는 경우에는 즉시 의사의 진료를 받는다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 리튬치료를 받고 있는 환자에게는 이 약의 규칙적인 사용을 피한다. 리튬치료와 병용 할 경우 갑상선기능 항진효과가 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 클로르헥시딘, 설파디아진ㆍ은화합물, 알칼리, 수은을 함유한 제제와 병용할 경우 이 약의 살균효과가 감소될 수 있으며 이는 변색을 통해 알 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 효소성분의 상처치료제와 병용시 효소의 효과가 상실된다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 이 약은 과산화수소와 병용하지 않는다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 임부 및 수유부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 임부 또는 임신하고 있을 가능성이 있는 부인 및 수유부에게 규칙적, 장기간 또는 광 범위하게 사용하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 흡수된 요오드는 태반장벽을 통해 유즙으로 이행된다. 흡수된 요오드는 태아나 신생아 의 갑상선기능에 영향을 미치거나 갑상선종을 일으킬 수 있다. 따라서 신생아 또는 영 아의 경우 갑상선기능을 점검하는 것이 권장된다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 임상검사치에의 영향">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[건강한 또는 손상된 피부를 통해 이 약의 요오드가 흡수되면 갑상선기능시험을 방해한 다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 과량투여시의 처치">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 증상]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[다량의 요오드를 경구복용한 경우 다음과 같은 증상이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[① 상복부통증, 구역, 구토 및 설사, 번갈다음증, 복부경련]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[② 긴장과다, 빈맥, 청색증, 두통, 어지러움, 헛소리, 허탈 및 혼수]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[③ 구강점막의 갈색화, 비염, 기관지염, 위장염, 출혈을 동반하는 점막손상, 성문(聲門) 부종, 신장출혈, 무뇨증, 치명적 혈관운동성 허탈 등이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[④ 과다한 요오드는 갑상선종 및 갑상선기능 저하 또는 항진을 나타낼 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 처치]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[① 해독을 위해서는 전분과 단백질을 함유한 음식을 섭취하는 것이 권장된다. 필요한 경우 3시간마다 5% 티오황산나트륨수화물으로 위세척을 하거나 10% 티오황산나트 륨수화물 10ml를 정맥주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[② 고의적 또는 사고로 다량섭취한 경우 전해질평형, 신장 및 갑상선기능에 특히 주 의하여 대증요법 및 지지요법을 실시한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="9. 적용상의 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 안과용 및 내복용으로 사용하지 않는다. 원액이 눈에 들어가지 않도록 주의하고 눈에 들어갔을 경우에는 즉시 깨끗한 물로 씻어낸다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 다른 제제와 혼합해서 사용하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 환부를 깨끗이 씻고 소독한다. 중증의 창상에 사용하는 경우의 희석액으로는 주사용 증류수 또는 멸균정제수를 사용한다(수도물이나 정제수를 사용하지 않는다.).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 의복 등에 이 약이 묻었을 경우 즉시 세척하면 물로 쉽게 씻겨진다. 합성섬유의 경우 암모니아수 또는 치오황산나트륨액으로 지울 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 비누류는 이 약의 살균작용을 약화시키므로 충분히 씻어 제거한 후 사용한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 건조되면 전기절연성이 되므로 전기메스를 사용하는 경우에는 이 약이 대극판과 피부 사이에 끼어들지 않도록 주의한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="10. 저장상의 주의사항">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 직사광선을 피하여 보존한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 저장용기로 금속제를 사용시 요오드의 산화특성에 의해 부식될 수 있다. 합성수지는 일반적으로 사용가능하나 대부분 가역적 변색은 나타날 수 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="11. 기타">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[질내에 사용으로 혈중 무기요오드치 및 혈중 총요오드치가 상승되었다는 보고가 있다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('포타딘(세정용)(포비돈요오드)', '197600173', '삼일제약(주)', '100밀리리터 중|포비돈요오드|KP|7.5|그램|유효요오드로서 0.75그램', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="(10% 액)(흡착면봉)(흡착탈지면)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 찢긴 상처, 화상, 창상의 살균소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 궤양, 농양의 살균소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 감염피부면의 소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 수술부위의 살균소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 주사 및 카테터부위의 소독]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(7.5% 액)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 찢긴 상처, 화상, 창상의 살균소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 감염피부면의 소독]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(세정액, 세액/7.5%)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 수술자의 손 및 팔의 살균소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 수술부위의 살균소독]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(에어로솔제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 찢긴 상처, 화상, 창상의 살균소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 감염피부면의 소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 수술부위의 살균소독]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(연고제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 찢긴 상처, 화상, 창상, 욕창의 살균소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 궤양, 농양의 살균소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 감염피부면의 살균소독]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 주사 및 카테터부위의 살균소독]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="(액제/10%)(흡착면봉)(흡착탈지면)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1일 수회 환부에 적당량 바른다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(액제/7.5%)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[감염부위를 물로 적신 후 이 약을 충분히 취하여 거품을 낸다. 거품을 약 3분간 적용시킨 후 씻어낸다. 1일 2 - 3회 또는 의사의 지시에 따라 사용한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(세정액, 세액/7.5%)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 수술자의 손, 팔의 살균소독 : 이 약 5mL를 취하여 손과 팔에 약 5분간 잘 문질러 거품을 충분히 낸 후 흐르는 물로 철저히 씻어낸다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 수술부위의 살균소독 : 이 약을 직접 바르거나 또는 적당량을 사용하여 잘 문질러 거품을 충분히 낸 후 멸균거즈로 닦아낸다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(에어로솔제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1일 1-수회 적당량을 피부와 25cm 떨어진 곳에서 환부에 표면이 덮일 정도로 분무한다. 사용전 잘 흔들어야 하며 피막형성시까지 방치하여 건조시킨다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(연고제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[환부에 1일 수회 환부에 적당량 바른다. 필요에 따라 드레싱 또는 붕대로 덮어 준다]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상의주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 다음 환자에는 투여하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약 및 이 약 성분에 과민증 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 갑상선기능 이상 환자(특히 결절성 갑상선종, 지방병성 갑상선종, 하시모토 갑상선염 등)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 신부전 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 신생아 및 6개월 미만의 영아]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 포진상 피부염 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 방사성요오드 치료 전후]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 신중히 투여할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 중증의 환자(광범위한 화상 환자 등)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 갑상선 질환의 병력이 있는 환자(특히 고령자)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 신부전의 병력이 있는 환자]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 부작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 과민증 : 드물게 아나필락시양 증상(호흡곤란, 홍조, 두드러기 등), 요오드진(iododerma) 등이 나타날 수 있으므로 이러한 경우에는 사용을 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 피부 : 때때로 동통, 가려움, 자극감, 발진, 발적, 피부염, 드물게 작열감, 피부궤양, 접촉피부염, 피부변색, 장기간 사용시 상처치유 저해가 나타날 수 있다. 이러한 증상이 심한 경우에는 사용을 중지한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 전해질, 대사 : 화상치료의 경우처럼 많은 양을 투여시 전해질 및 혈청의 삼투압장애, 대사성 산증이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 신장 : 화상치료의 경우처럼 많은 양을 투여시 신부전을 일으킬 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 혈액 : 장기간 사용시 시상하부-뇌하수체-갑상선-축 기능의 변화와 가역반응에 의한 요오드의 분비량 증가가 나타날 수 있다. 또한 혈청 갑상선 자극호르몬의 농도가 약간 상승하고 T<sub>4</sub> 및 T<sub>3</sub>의 감소가 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 대량 투여 : 대량 또는 장기간의 접촉으로 일부 환자에서 일시적인 혈청 요오드 결합단백의 증가 보고가 있다. 광범위한 상해 또는 화상에 이 약의 반복적 투여로 요오드의 전신 흡수에 따른 여러 부작용(금속 맛을 느낌, 타액분비 증가, 목과 입에서의 작열감 및 동통, 눈의 자극 및 부종, 피부 반응, 배탈, 설사 및 폐부종으로 인한 호흡곤란)이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 기타 : 소독용으로 이 약을 과량 사용시 요오드 독성이 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 일반적주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 분만 직전에 사용한 경우에는 분만후 되도록 빨리 신생아의 눈을 씻어주고 목욕시켜 부착되지 않도록 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약에 의한 치료는 보존적 치료임을 유의하고 증상의 개선 경향이 보이지 않을 경우에는 외과적 요법 등을 고려한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 광범위한 화상을 입은 환자에게 이 약을 사용할 경우 수분공급이 충분히 이루어져야 한다. 산, 염기 대사 및 신기능을 관찰해야 하며 대사성 산증이나 신기능 이상이 있을 경우 이 약의 사용을 중지한다(세정액, 세액/7.5% 제외).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 이 약을 화상으로 인한 궤양에 사용할 경우 화상 후의 2차 손상에 의해 발생하는 궤양에만 사용하며 새로 발생한 화상에 대해서는 다른 적절한 요법을 고려한다(세정액, 세액/7.5% 제외).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 이 약은 섬광촬영술을 이용한 갑상선 진단에 영향을 줄 수 있으므로 이 약의 투여와 섬광도 조사는 1-2주의 간격을 두고 실시한다. 또한 이 약의 투여 중에는 갑상선기능이 가역적으로 변화될 수 있으므로 주의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 이 약은 요오드함유 제제이므로 대량투여 및 장기연용시에는 갑상선기능 이상에 주의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 감염(복막염, 골수염 등) 발생 시에는 이 약의 사용 전 생리식염수로 세정한다. 이 약의 접촉으로 박테리아로부터 내독소가 유리될 위험이 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 드물게 쇽(아나필락시)이 일어날 수 있으므로 사용후 갑자기 가려움, 부종, 가슴쓰림 등과 동시에 안색창백, 수족냉감, 식은 땀, 숨가쁨 등의 중증의 증상이 나타나는 경우에는 즉시 의상의 진료를 받는다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 리튬치료를 받고있는 환자에게는 이 약의 규칙적인 사용을 피한다. 리튬 치료와 병용할 경우 갑상선기능 항진효과가 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 클로르헥시딘, 설파디아진ㆍ은화합물, 알칼리, 수은을 함유한 제제와 병용할 경우 이 약의 살균효과가 감소될 수 있으며 이는 변색을 통해 알 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 효소성분의 상처치료제와 병용시 효소의 효과가 상실된다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 이 약은 과산화수소와 병용하지 않는다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 임부 및 수유부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 임부 또는 임신하고 있을 가능성이 있는 부인 및 수유부에게 규칙적, 장기간 또는 광범위하게 사용하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 흡수된 요오드는 태반장벽을 통해 유즙으로 이행된다. 흡수된 요오드는 태아나 신생아의 갑상선기능에 영향을 미치거나 갑상선종을 일으킬 수 있다. 따라서 신생아 또는 영아의 경우 갑상선기능을 점검하는 것이 권장된다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 임상검사치에의 영향">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[건강한 또는 손상된 피부를 통해 이 약의 요오드가 흡수되면 갑상선기능 시험을 방해한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 과량투여시의 처치">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 증상]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[다량의 요오드를 경구복용한 경우 다음과 같은 증상이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[① 상복부통증, 구역, 구토 및 설사, 번갈다음증, 복부경련]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[② 긴장과다, 빈맥, 청색증, 두통, 어지러움, 헛소리, 허탈 및 혼수]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[③ 구강 점막의 갈색화, 비염, 기관지염, 위장염, 출혈을 동반하는 점막 손상, 성문(聲門)부종, 신장출혈, 무뇨증, 치명적 혈관운동성 허탈 등이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[④ 과다한 요오드는 갑상선종 및 갑상선기능저하 또는 항진을 나타낼 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 처치]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[① 해독을 위해서는 전분과 단백질을 함유한 음식을 섭취하는 것이 권장된다. 필요한 경우 3시간마다 5% 티오황산나트륨수화물로 위세척을 하거나 10% 티오황산나트륨수화물 10mL를 정맥주사한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[② 고의적 또는 사고로 다량 섭취한 경우 전해질 평형, 신장 및 갑상선기능에 특히 주의하여 대증요법 및 지지요법을 실시한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="9. 적용상의 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 안과용 및 내복용으로 사용하지 않는다. 원액이 눈에 들어가지 않도록 주의하고 눈에 들어갔을 경우에는 즉시 깨끗한 물로 씻어낸다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 다른 제제와 혼합해서 사용하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 환부를 깨끗이 씻고 소독한다. 심한 창상에 사용하는 경우의 희석액으로는 주사용 증류수 또는 멸균정제수를 사용한다(수도물이나 정제수를 사용하지 않는다).]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 의복 등에 이 약이 묻었을 경우 즉시 세척하면 물로 쉽게 씻겨진다. 합성섬유의 경우 암모니아수 또는 티오황산나트륨수화물액으로 지울 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 비누류는 이 약의 살균작용을 약화시키므로 충분히 씻어 제거한 후 사용한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 건조되면 전기 절연성이 되므로 전기메스를 사용하는 경우에는 이 약이 대극판과 피부사이에 끼어들지 않도록 주의한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="10. 저장상의 주의사항">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 직사광선을 피하여 보존한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 저장용기로 금속제를 사용시 요오드의 산화특성에 의해 부식될 수 있다. 합성수지는 일반적으로 사용가능하나 대부분 가역적 변색은 나타날 수 있다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="11. 기타">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[질내에 사용으로 혈중 무기요오드치 및 혈중 총요오드치가 상승되었다는 보고가 있다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('액티피드시럽', '197600174', '삼일제약(주)', '100mL 중|슈도에페드린염산염|BP|600|밀리그램|;100mL 중|트리프롤리딘염산염수화물|BP|25|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="(정제)(시럽제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[코감기, 알레르기 및 혈관운동성 코염에 의한 다음 증상의 완화 : 재채기, 콧물, 코막힘, 눈물]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(캡슐제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[코감기, 알레르기 및 혈관운동성 코염에 의한 다음 증상의 완화 : 재채기, 콧물, 코막힘]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="(정제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[성인 : 1회 1정]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[소아(12 &#12316; 14세) : 1회 1/2정]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1일 3회 식후에 복용한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(캡슐제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[성인 : 1회 1캅셀]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[소아(12 &#12316; 14세) : 1회 1/2캅셀]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1일 3회 식후에 복용한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(시럽제)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[성인 및 12세 이상의 소아 : 1회 10 mL]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[12세 미만 소아 : 의사의 진료를 받는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1일 3회 식후에 복용한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상의주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 경고">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[슈도에페드린 함유 의약품 복용시 급성 전신성 발진성 농포증(AGEP)과 같은 중증 피부 이상반응이 나타날 수 있다. 발열, 홍반, 다수의 작은 농포와 같은 증상이 관찰될 경우 이 약의 복용을 중단하고 의사 또는 약사와 상의해야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음과 같은 사람은 이 약을 복용하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약에 과민반응 병력이 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 중증(심한 증상)의 간 및 신기능 장애 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 중증(심한 증상)의 관상 심장 질환 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 잔뇨 형성을 수반하는 배뇨(소변을 눔)장애 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 고혈압 및 관상동맥(심장동맥)질환 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 임부 또는 임신하고 있을 가능성이 있는 여성, 수유부]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 2세 이하의 영아(갓난아기)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) MAO 억제제를 투여중인 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[9) 파라옥시안식향산메칠에 과민반응 환자(파라옥시안식향산메칠 함유제제에 한함)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 이 약을 복용하는 동안 다음의 약을 복용하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 혈압강하제, 알코올, 중추신경억제제(수면제, 진정제, 안정제), MAO 억제제, 교감신경흥분제, 삼환계 항우울약, 구아네티딘, 브레틸리움, 베타나이드, 데브리소퀸, 메칠도파, 교감신경성약(충혈제거제, 식욕억제제, 암페타민류의 신경자극제), α 및 β 아드레날린 저해제(억제제)와 병용(함께 복용)투여하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) MAO억제제의 긴 작용시간 때문에 MAO억제제 투여 최소 15일 후 이 약을 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 이 약을 복용하는 동안 다음의 행위를 하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[이 약의 투여 중 자동차운전 및 위험한 기계조작 등 주의력을 집중시킬 필요가 있는 일에 종사하지 않도록 주의한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 다음과 같은 사람은 이 약을 복용하기 전에 의사, 치과의사, 약사와 상의할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 천식 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 녹내장 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 심질환 및 부정맥 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 당뇨병, 갑상샘기능항진증 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 전립샘비대로 인한 배뇨(소변을 눔)곤란 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 12세 미만의 소아]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 다음과 같은 경우 이 약의 복용을 즉각 중지하고 의사, 치과의사, 약사와 상의할 것. 상담 시 가능한 한 이 첨부문서를 소지할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 7일 이내에 증상의 개선이 나타나지 않거나 고열을 동반할 경우]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 진정, 졸음, 중추 신경 쇠약, 중추 흥분, 불안, 발한(땀이 남), 경미한(가벼운) 혈압강하, 시력장애, 산동(동공확대), 헛소리, 환각, 흥분, 근육경련, 경직(굳음), 아테토시스, 간대상 및 강직성 경련, 고열, 호흡마비, 혼수, 순환기 마비 등의 중추신경계 이상이 나타날 경우]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 피부반응, 일시적인 자극통, 작열감(화끈감) 등의 과민반응이 나타날 경우]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 구갈, 구역, 구토 등의 소화기계 이상이 나타날 경우]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 배뇨(소변을 눔)곤란 등의 이상이 나타날 경우]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 소아에서 특히 흥분이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 급성 전신성 발진성 농포증(AGEP), 발열, 홍반, 다수의 작은 농포와 같은 중증 피부 이상반응이 나타날 경우]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[8) 슈도에페드린 성분과 관련하여 허혈성 대장염의 증상(급격한 복통, 직장 출혈 등)이 발현될 경우]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 기타 이 약의 복용시 주의할 사항.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[과량투여 시 급성 중독(환각 및 경련), 신경과민, 어지러움, 불면증 등을 일으킬 수 있으며 이러한 증상이 나타날 경우에는 위세척, 방광 카테터 삽입법을 사용한다. 슈도에페드린 제거에는 투석하도록 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 저장상의 주의사항">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 소아의 손이 닿지 않는 곳에 보관한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 직사일광을 피하고 될 수 있는 대로 습기가 적고 서늘한 곳에 밀전하여(뚜껑을 꼭 닫아) 보관한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 의약품을 원래 용기에서 꺼내어 다른 용기에 보관하는 것은 의약품 오용에 따른 사고발생이나 의약품 품질 저하의 원인이 될 수 있으므로 원래의 용기에 넣고 꼭 닫아 보관할 것.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('페리나정', '197600345', '삼진제약(주)', '1정(182.3mg) 중|수도에페드린염산염|USP|25.00|밀리그램|;1정(182.3mg) 중|클로르페니라민말레산염|KP|4.00|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[감기 또는 알레르기성 및 혈관운동성 비염(코염)에 의한 다음 증상의 완화 : 재채기, 콧물, 코막힘, 눈물]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[성인 1회 1정 1일 3회 식후에 복용한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상의주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 경고">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[슈도에페드린 함유 의약품 복용시 급성 전신성 발진성 농포증(AGEP)과 같은 중증 피부 이상반응이 나타날 수 있다. 발열, 홍반, 다수의 작은 농포와 같은 증상이 관찰될 경우 이 약의 복용을 중단하고 의사 또는 약사와 상의해야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 투여하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약에 과민증 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약은 유당(젖당)을 함유하고 있으므로, 갈락토오스 불내성(galactose intolerance), Lapp 유당(젖당)분해효소 결핍증(Lapplactase deficiency) 또는 포도당-갈락토오스 흡수장애(glucose-galactose malabsorption) 등의 유전적인 문제가 있는 환자에게는 투여하면 안 된다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 다음 환자에는 신중히 투여할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 천식, 녹내장, 고혈압, 심질환, 관상동맥(심장동맥)질환, 당뇨병, 갑상선기능항진증, 전립선(전립샘) 비대로 인한 배뇨(소변을 눔)장애시 및 7세 이하의 소아에게는 투여하지 않는 것이 바람직하나 반드시 필요한 경우에는 약사 또는 의사의 지시에 따라 투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 임부 또는 임신하고 있을 가능성이 있는 부인, 수유부]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 부작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 졸음, 일과성(한번 나타나고 없어지는) 자극통, 구갈(목마름), 구역, 불안, 발한(땀이 남) 및 약간의 혈압강하 등을 일으킬 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 과량투여시 급성중독(환각 및 경련), 신경과민, 어지러움, 불면 등을 일으킬 수 있으므로 권장량 이상을 초과하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 소아에게는 특히 흥분이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 급성 전신성 발진성 농포증(AGEP), 발열, 홍반, 다수의 작은 농포와 같은 중증 피부 이상반응이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 슈도에페드린 성분과 관련하여 허혈성 대장염의 증상(급격한 복통, 직장 출혈 등)이 발현될 수 있다. 이 경우 이 약의 복용을 중단해야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 일반적 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약을 투여하는 동안 졸음이 나타날 수 있으므로 자동차운전 등 위험이 수반되는 기계조작 을 하지 않도록 주의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약의 투여중에는 알코올성 음료를 피한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 사고로 과량투여시에는 약사 또는 의사와 상의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 7일 이내에 증상의 개선이 나타나지 않거나 고열을 동반할 때에는 연용(계속 복용)전에 약사 또는 의사와 상의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 7세 이하의 소아에게는 필요한 경우 시럽제를 투여하되 용법·용량을 잘 지키고 표시된 연령 (나이)이외의 소아에게는 투여하지 않는다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[다음 약들과 병용(함께 복용)투여하지 않는다 : 혈압강하제, 알코올, 중추신경억제제(수면제, 진정제, 안정제), MAO저해제(억제제) 등의 항우울제]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 저장상의 주의사항">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 소아의 손이 닿지 않는 곳에 보관한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 직사일광을 피하고 되도록이면 습기가 적은 서늘한 곳에 밀전하여(뚜껑을 꼭 닫아)보관한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 오용(잘못 사용)을 막고 품질의 보존을 위하여 다른 용기에 바꾸어 넣지 않는다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('홈타민연질캡슐(수출용)(수출명:파마톨포르트연질캡슐,ProVitonS.Caps.)', '197600350', '한국유나이티드제약(주)', '1캡슐 (1,374그램) 중|레티놀팔미테이트|KP|4000|아이.유|;1캡슐 (1,374그램) 중|에르고칼시페롤|KP|400|아이.유|;1캡슐 (1,374그램) 중|토코페롤아세테이트|KP|15|밀리그램|;1캡슐 (1,374그램) 중|아스코르브산|KP|75|밀리그램|;1캡슐 (1,374그램) 중|티아민염산염|KP|2|밀리그램|;1캡슐 (1,374그램) 중|리보플라빈|KP|2|밀리그램|;1캡슐 (1,374그램) 중|피리독신염산염|KP|2|밀리그램|;1캡슐 (1,374그램) 중|판토텐산칼슘|BP|10|밀리그램|;1캡슐 (1,374그램) 중|니코틴산아미드|KP|20|밀리그램|;1캡슐 (1,374그램) 중|시아노코발라민|KP|5|밀리그램|;1캡슐 (1,374그램) 중|폴산|KP|100|마이크로그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[허약체질, 식욕부진, 영양불량, 소모설질환, 피로,]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[병중ㆍ병후, 임신ㆍ수유기, 발육기 및 노년기의 비타민 보급]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[성인 및 8세 이상 어린이 1회 1캅셀, 1회 복용한다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 경고">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[임부에 비타민 A(레티놀)를 1일 5,000IU이상 투여하는 경우에는 선천성 기형을 유발할 위험이 있으므로 임신 3개월 이내 또는 임신할 가능성이 있는 부인에는 비타민A를 5,000IU/일 이상 투여하지 말 것.(비타민 A 결핍증 환자는 제외)]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 환자에는 투여하지 말 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 대두유에 과민하거나 알레르기 병력이 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 콩 또는 땅콩에 과민증이 있는 환자]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 다음 환자에는 신중히 투여할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 고지단백혈증, 당뇨병성고지질혈증 및 췌장염 등 지방대사 이상 환자 또는 지질성 유제를 신중히 투여해야 하는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 이 약은 황색5호(선셋옐로우 FCF, Sunset Yellow FCF)를 함유하고 있으므로 이 성분에 과민하거나 알레르기 병력이 있는 환자에는 신중히 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 다음 사람은 복용 전에 의사 또는 약사와 상의할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 1세 미만의 유아]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 의사의 치료를 받고 있는 사람]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 임신부 또는 임신하고 있을 가능성이 있는 부인]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 복용시 다음 사항에 주의할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 정해진 용법, 용량을 지킬 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 소아에 복용시킬 경우에는 보호자의 지도, 감독하에 복용시킬 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 본 제제에 함유된 비타민 A는 정상적인 식이에서 충분히 공급되므로 보조요법의 용량은 비타민 A로서 1일 8,000단위 이상을 넘지 않도록 할 것.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 복용중 또는 복용후는 다음 사항에 주의할 것.">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 본제의 복용에 의해 구역, 구토, 설사, 가려움증 등의 증상이 나타나는 경우에는 복용을 중지하고 의사 또는 약사와 상담할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 본제의 복용에 의해 불쾌감, 설사, 변비, 발적 등의 증상이 나타나는 경우에는 복용을 중지하고 의사 또는 약사와 상담할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 이 약의 복용에 의해 생리가 예정보다 빨라지거나, 양이 점점 많아질 수도 있다. 출혈이 오래 계속될 경우에는 의사 또는 약사와 상담할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 일정간 복용하여도 증상이 개선되지 않으면 의사 또는 약사와 상의할 것.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 일반적 주의(경구제, 주사제 및 질연질캡슐제에 한함)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[지방과부하로 특별한 위험이 예상되는 환자에게 이 약을 투여할 때 혈장지질치를 점검할 것을 권장한다. 이 점검을 통해 지방의 체외배설이 불충분하다고 판단될 경우에는 이 약의 투여를 적절히 조절한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[환자가 다른 정주용 지질제를 동시에 투여받고 있다면 이 약 중의 부형제로 혼재되어 있는 지질의 양을 고려하여 그 지질제의 투여량을 감소해야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 임부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[외국에서 임신전 3개월로부터 임신 초기 3개월까지 비타민 A를 10,000IU/일 이상 섭취한 여성으로부터 태어난 아이에서 두부신경릉 등을 중심으로 하는 기형발현 증가가 추정된다는 역학조사 결과가 있으므로 임신 3개월 이내 또는 임신을 희망하는 부인에 투여할 경우는 용법, 용량에 주의하고 이 약에 의한 비타민A 투여는 5,000IU/일 미만에 머물도록 하는 등 적절한 주의를 기울일 것.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="9. 보관 및 취급상의 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 어린이의 손에 닿지 않는 곳에 보관할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 직사일광을 피하고 될 수 있는 대로(습기가 적고) 서늘한 곳에(밀전하여) 보관할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 오용을 막고 품질을 보호, 유지하기 위해 다른 용기에 넣지 말 것.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('로프민캡슐(로페라미드염산염)', '197600412', '영일제약(주)', '1캡슐140밀리그램|로페라미드염산염|KP|2|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="1. 주효능 효과">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[급성설사, 만성설사]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="(정제, 캅셀제)">
    <ARTICLE title="ㆍ성인">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 급성설사 : 초회량은 염산로페라미드로서 4mg을 경구투여하고, 유지량으로는 묽은 변이 있을 때마다 2mg씩을 투여한다. 1일 상용량은 6-8mg이며, 1일 최대투여량은 16mg이다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 만성설사 : 초회량은 염산로페라미드로서 4mg을 경구투여하고, 설사가 치료될 때까지 묽은 변이 있을 때마다 2mg을 투여한다. 그 이후에는 환자 개별적으로 필요에 맞도록 용량을 감소시켜야 한다. 1일 최적투여량이 결정되면 이를 1일 1회 또는 1일 2회로 나누어 투여한다. 보통 유지용량은 1일 2-6mg이며, 1일 최대용량은 16mg이다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="ㆍ소아(9-12세)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 급성설사 : 초회량은 염산로페라미드로서 2mg을 경구투여하고, 유지량으로는 묽은 변이 있을 때마다 2mg씩을 투여한다. 1일 최대용량은 6mg이다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="ㆍ연령, 증상에 따라 적절히 증감한다." />
  </SECTION>
  <SECTION title="(시럽제)">
    <ARTICLE title="ㆍ소아(24개월-12세)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[- 급성설사 : 첫째날의 용량은 아래의 표와 같고 둘째날부터는 묽은 변이 있을 경우에만 체중 kg당 0.1mg을 투여하되 첫째날의 용량을 초과하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="table" textIndent="" marginLeft=""><![CDATA[<tbody> 
 <tr> 
  <td> <p>연 령</p> </td> 
  <td> <p>첫째날의 용량</p> </td> 
 </tr> 
 <tr> 
  <td> <p>2 - 5 세 (13-20kg)</p> </td> 
  <td> <p>1mg씩 3회 (1일 3mg)</p> </td> 
 </tr> 
 <tr> 
  <td> <p>6 - 8 세 (20-30kg)</p> </td> 
  <td> <p>2mg씩 2회 (1일 4mg)</p> </td> 
 </tr> 
 <tr> 
  <td> <p>9 - 12세 (30kg이상)</p> </td> 
  <td> <p>2mg씩 3회 (1일 6mg)</p> </td> 
 </tr> 
</tbody>]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="ㆍ연령, 증상에 따라 적절히 증감한다." />
  </SECTION>
</DOC>', '<DOC title="사용상주의사항" type="NB">
  <SECTION title="">
    <ARTICLE title="1. 다음 환자에는 투여하지 말 것">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 24개월 미만의 영아]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 소아(만성설사인 경우)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 혈변 및 고열이 있는 세균성 설사 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 급성 궤양성 대장염 또는 광범위 항생물질 투여와 관련된 위막성 대장염 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 장폐색증, 거대결장증, 중독성거대결장증 등을 포함하는 중요한 후유증 위험의 가능성 때문에 연동억제를 피해야 하는 환자에 투여하지 않으며, 변비, 복부팽만 혹은 장폐색증이 나타나면 이 약을 즉시 중단해야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 이 약에 과민증의 병력이 있는 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7) 출혈성 대장염 환자 [장관출혈성대장균(O-157)이나 이질균, 살모넬라, 시겔라, 캄필로박터 등의 위독한 세균성 설사환자의 증상악화, 치료기간 연장을 초래할 우려가 있다]]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 경우에는 신중히 투여할 것">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[간장애 환자]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="3. 부작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 임상시험 중 보고된 이상반응]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[약물과의 인과관계 평가에 관계 없이, 보고된 이상반응을 요약하였다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(1) 급성 설사 환자에서 1% 이상의 빈도이면서 위약군과 동등 이상의 빈도로 보고된 이상반응]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[이 약보다 위약 투여 환자에서 더 자주 보고된 이상반응: 구갈, 고창, 복부경련 및 급통증.]]></PARAGRAPH>
      <PARAGRAPH tagName="table" textIndent="" marginLeft=""><![CDATA[<tbody> 
 <tr> 
  <td>&nbsp;</td> 
  <td> <p>염산 로페라미드</p> </td> 
  <td> <p>위약</p> </td> 
 </tr> 
 <tr> 
  <td> <p>치료 환자 수</p> </td> 
  <td> <p>231</p> </td> 
  <td> <p>236</p> </td> 
 </tr> 
 <tr> 
  <td> <p>위장관계</p> <p>변 비</p> </td> 
  <td> <p>2.6%</p> </td> 
  <td> <p>0.8%</p> </td> 
 </tr> 
</tbody>]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(2) 만성 설사 환자에서 1% 이상의 빈도이면서 위약군과 동등 이상의 빈도로 보고된이상반응]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[이 약보다 위약 투여 환자에서 더 자주 보고된 이상반응: 구역, 구토, 두통, 고장(meteorism), 복통, 복부경련 및 급통증.]]></PARAGRAPH>
      <PARAGRAPH tagName="table" textIndent="" marginLeft=""><![CDATA[<tbody> 
 <tr> 
  <td>&nbsp;</td> 
  <td> <p>염산 로페라미드</p> </td> 
  <td> <p>위약</p> </td> 
 </tr> 
 <tr> 
  <td> <p>치료 환자 수</p> </td> 
  <td> <p>285</p> </td> 
  <td> <p>277</p> </td> 
 </tr> 
 <tr> 
  <td> <p>위장관계</p> <p>변 비</p> </td> 
  <td> <p>5.3%</p> </td> 
  <td> <p>0.0%</p> </td> 
 </tr> 
 <tr> 
  <td> <p>중추 및 말초신경계</p> <p>어지러움</p> </td> 
  <td> <p>1.4%</p> </td> 
  <td> <p>0.7%</p> </td> 
 </tr> 
</tbody>]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(3) 급 · 만성 설사 환자를 대상으로 한, 76건의 대조 혹은 단독 임상시험에서 1% 이상의 빈도로 보고된 이상반응]]></PARAGRAPH>
      <PARAGRAPH tagName="table" textIndent="" marginLeft=""><![CDATA[<tbody> 
 <tr> 
  <td>&nbsp;</td> 
  <td> <p>급성 설사</p> </td> 
  <td> <p>만성 설사</p> </td> 
  <td> <p>모든 임상시험<sup>a</sup></p> </td> 
 </tr> 
 <tr> 
  <td> <p>치료 환자 수</p> </td> 
  <td> <p>1913</p> </td> 
  <td> <p>1371</p> </td> 
  <td> <p>3740</p> </td> 
 </tr> 
 <tr> 
  <td> <p>위장관계</p> <p>구 역</p> <p>변 비</p> <p>복 부 경 련</p> </td> 
  <td> <p>0.7%</p> <p>1.6%</p> <p>0.5%</p> </td> 
  <td> <p>3.2%</p> <p>1.9%</p> <p>3.0%</p> </td> 
  <td> <p>1.8%</p> <p>1.7%</p> <p>1.4%</p> </td> 
 </tr> 
</tbody>]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[<sup>a</sup> 급성 설사인지 만성설사인지 언급되지 않은 환자를 포함하여 모든 임상시험에 참여한 모든 환자]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 시판후 보고된 이상반응]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[시판후에 자발적으로 보고된 이상반응을 신체기관별 및 빈도에 따라 열거하였다. 표시된 빈도는 다음과 같다; 매우 일반적인(&gt;1/10), 일반적인(1/100~1/10), 일반적이지 않은(1/1000~ 1/100), 드문(1/10000~1/1000), 매우 드문(&lt;1/10000, 이례적인 반응 포함). 이 빈도는 자발적 보고 비율이며 임상시험이나 역학연구에서 보이는 실제 빈도를 나타내는 것은 아니다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(1) 피부 및 부속기: 매우 드물게 발진, 두드러기, 소양감, 이례적으로 혈관부종, 중독성표피박리, 다형성 홍반, 스티븐스-존슨 증후군을 포함하는 수포성 발진이 보고되었다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(2) 신체 전반: 이례적으로 알레르기 반응이 발생되었으며, 일부 사례에서는 아나필락시스 쇽 및 아나필락시스 양 반응을 포함하는 중증의 과민반응이 보고되었다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(3) 위장관계 : 매우 드물게 복통, 장폐색증, 복부팽만, 구역, 변비, 구토, 중독성 거대결장증을 포함하는 거대결장증, 고창 및 소화불량이 보고되었다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(4) 비뇨생식계: 이례적으로 뇨저류가 보고되었다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(5) 정신계: 매우 드물게 졸음이 보고되었다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="1"><![CDATA[(6) 중추 및 말초 신경계: 매우 드물게 어지러움이 보고되었다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 보고된 이상반응 중 많은 증상이 설사 환자에서 흔히 나타나는 증상들(복통/복부불편감, 구역, 구토, 구갈, 피로, 졸음, 어지러움, 변비, 고창)이어서 질병에 의한 증상과 약물에 의한 이상반응을 구분하는 것이 어려운 경우가 많다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="4. 일반적 주의">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 이 약은 설사의 원인 치료제가 아닌 대증요법제이므로 급성설사의 경우 48시간 이내, 만성 설사의 경우 10일 이내에 증상이 개선되지 않으면 투여를 중지하고 약사 또는 의사와 상의한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 설사 환자는 체액 및 전해질 손실이 나타날 수 있고, 이러한 증상은 특히 소아에게는 심각한 문제를 발생시킬 수 있으므로 이러한 경우 적합한 수액 및 전해질을 보충해 주도록 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 간기능 장애 환자에서의 이 약의 약동학은 밝혀져 있지 않지만, 이러한 환자에서는 간에서의 1차 대사율(first pass effect)이 저하되어 있으므로 주의해서 투여해야 하며 중추신경계 부작용에 대하여 면밀히 관찰해야 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[4) 이 약으로 설사 치료 중 피로, 어지러움, 졸음이 나타날 수 있으므로 운전이나 기계조작을 할 때는 주의해서 투여할 것이 권장된다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[5) 이 약으로 설사 치료중인 AIDS 환자는 복부팽만 징후가 나타나는 초기에 치료를 중단해야 한다. 바이러스 및 세균에 의한 감염성 대장염을 가진 AIDS 환자에서 염산로페라미드로 치료중 중독성 거대결장증의 이례적인 보고가 있었다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[6) 이 약은 대부분 대사된 후 대사체 혹은 미변화체가 대변으로 배설되므로, 신장 질환 환자는 용량 조절이 요구되지 않는다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="5. 상호작용">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[비임상자료에서 로페라미드가 P-glycoprotein의 기질임을 보여주고 있다. P-glycoprotein 저해제인 퀴니딘이나 리토나비르를 로페라미드(16mg 1회 투여)와 병용투여시 로페라미드의 혈장농도가 2~3배 상승되었다. 로페라미드를 권장용량(2mg, 1일 최대 16mg)으로 투여할 때 이와 같은 P-glycoprotein 저해제와의 약동학적 상호작용의 임상적 유의성은 확립되지 않았다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="6. 임부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[동물실험에서 이 약이 기형발생을 나타내지 않았으나 임신중 특히 임신 첫 3개월 이내의 임부에 대하여는 치료상의 유익성이 위험성을 상회하는 경우에만 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="7. 수유부에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[이 약을 투여한 수유부의 경우 이 약의 소량이 모유중으로 분비될 수 있으므로 이 약은 수유중에는 권장되지 않는다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="8. 어린이에 대한 투여(시럽제에 한함)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 24개월 이상 7세 이하의 영 · 유아에는 반드시 약사 또는 의사의 지시하에 투여할 것.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 24개월 이상 7세 이하의 영 · 유아의 경우, 간기능 미숙으로 인한 상대적 과량투여 현상이 나타날 수 있으므로 이러한 증상이 나타나면 투여를 중지하는 등 과량투여 항에 따른 처치를 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3) 24개월 이상 7세 이하의 영 · 유아의 경우에는 12시간이상 변을 보지 않으면 투여를 중지한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="9. 고령자에 대한 투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[일반적으로 고령자는 생리기능이 저하되어 있으므로 용량에 주의하는 등 주의해서 투여한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="10. 과량투여">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1) 증상: 과량투여에 의해 중추신경 억제(혼미, 축동, 근긴장증, 경면, 호흡저하, 협조운동부전(協助運動不全), 졸음), 뇨저류 및 장폐색증이 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[소아의 경우 중추신경계에 대한 효과가 성인보다 예민하게 나타날 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[로페라미드염산염의 오·남용에 의한 과량(일일 복용량이 40mg부터 792mg까지 보고됨) 복용시 QT간격 연장, 중대한 심실부정맥, 사망사례가 보고되었으므로 과량으로 복용하지 않는다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[2) 처치: 과량투여의 증상이 나타나면 해독제로서 염산나록손을 투여할 수 있다. 이 약의 작용시간이 나록손(1~3시간)보다 길기 때문에 나록손의 반복 투여가 적절할 수 있다. 중추신경계 억제 효과를 관찰하기 위해 적어도 48시간 동안은 계속해서 관찰해야 한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="11. 적용상의 주의(정제, 캅셀제에 한함)">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7세 이하의 영 · 유아에게 정제 및 캅셀제를 투여하는 것은 바람직하지 않다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="12. 기타">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[인체에서 약물 의존성이 관찰되지 않았으나 동물실험에서 과량투여에 의하여 모르핀성 약물의존성이 나타났다는 보고가 있다.]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>
');
INSERT INTO `pill` (name, code, company, basis, effect, capacity, caution) VALUES ('경동아세트아미노펜정(수출용)', '197600432', '경동제약(주)', '1정(400mg) 중|아세트아미노펜|KP|300|밀리그램|', '<DOC title="효능효과" type="EE">
  <SECTION title="">
    <ARTICLE title="1. 주효능 효과">
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[감기로 인한 발열 및 동통, 두통, 신경통, 근육통, 월경통, 염좌통]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="2. 다음 질환에도 사용할 수 있다.">
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="0"><![CDATA[치통, 관절통, 류마티양 동통]]></PARAGRAPH>
    </ARTICLE>
  </SECTION>
</DOC>', '<DOC title="용법용량" type="UD">
  <SECTION title="">
    <ARTICLE title="(경구 : 300mg, 325mg, 500mg )">
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[ㆍ성인 : 아세트아미노펜으로서 1회 0.3-1.0g 1일 3-4회 경구투여한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[ㆍ1일 최고 4g까지 투여할 수 있다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[ㆍ연령, 질환, 증상에 따라 적절히 증감한다.]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(어린이용 : 80mg, 160mg )">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[ㆍ다음 1회 용량을 1일 3-4회 경구투여 한다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="0" marginLeft="2"><![CDATA[(160mg) (80mg)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[11-14세 : 200-400mg (2정) (4정)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[7-10세 : 150-300mg (1-2정) (2-4정)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3- 6세 : 100-200mg (1정) (2정)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[1- 2세 : 60-120mg (1정)]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[3개월-1세미만: 30-60mg]]></PARAGRAPH>
    </ARTICLE>
    <ARTICLE title="(어린이용 : 액제, 현탁액, 엘릭서제 )">
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[소아 : 이 약은 1회 10 ~15mg/kg의 용량을 4~6시간 간격으로 필요시 복용하며, 1일 최대 5회 (75mg/kg)를 넘지 않는다. 이 약 1회 용량은 몸무게 또는 나이에 따라 다음과 같다.]]></PARAGRAPH>
      <PARAGRAPH tagName="p" textIndent="" marginLeft=""><![CDATA[(몸무게를 아는 경우에는 몸무게에 따른 용량으로 복용하는 것이 더 적절하다.)]]></PARAGRAPH>
      <PARAGRAPH tagName="table" textIndent="" marginLeft=""><![CDATA[<tbody> 
 <tr> 
  <td> <p>연령</p> </td> 
  <td> <p>몸무게(kg)</p> </td> 
  <td> <p>아세트아미노펜으로서 용량</p> </td> 
  <td> <p>1회용량</p> </td> 
 </tr> 
 <tr> 
  <td> <p>만 12세</p> </td> 
  <td> <p>43kg 이상</p> </td> 
  <td> <p>640mg</p> </td> 
  <td> <p>20ml</p> </td> 
 </tr> 
 <tr> 
  <td> <p>만 11세</p> </td> 
  <td> <p>38.0~42.9kg</p> </td> 
  <td> <p>480mg</p> </td> 
  <td> <p>15ml</p> </td> 
 </tr> 
 <tr> 
  <td> <p>만 9-10세</p> </td> 
  <td> <p>30.0~37.9kg</p> </td> 
  <td> <p>400mg</p> </td> 
  <td> <p>12.5ml</p> </td> 
 </tr> 
 <tr> 
  <td> <p>만 6-8세</p> </td> 
  <td> <p>21.0~29.9kg</p> </td> 
  <td> <p>320mg</p> </td> 
  <td> <p>10ml</p> </td> 
 </tr> 
 <tr> 
  <td> <p>만 4-5세</p> </td> 
  <td> <p>16.0~20.9kg</p> </td> 
  <td> <p>240mg</p> </td> 
  <td> <p>7.5ml</p> </td> 
 </tr> 
 <tr> 
  <td> <p>만 2-3세</p> </td> 
  <td> <p>12.0~15.9kg</p> </td> 
  <td> <p>160mg</p> </td> 
